import React, {useEffect, useRef, useState} from 'react';
import {FuseAnimateGroup, FusePageSimple} from '@fuse';
import {Menu, MenuItem, Hidden, Icon, IconButton, Tab, Tabs, Typography} from '@material-ui/core';
import {makeStyles} from '@material-ui/styles';
import Clock from '../Clock'

import Offerings from './widgets/Offerings'
import PendingRequests from './widgets/PendingRequests'
import CoursesAvailabe from './widgets/CoursesAvailable'
import CoursesEnrolling from './widgets/CoursesEnrolling'
import StudentsRequestsRejected from './widgets/StudentsRequestRejected'
import StudentsRequestsApproved from './widgets/StudentsRequestsApproved'
import StudentsRequestPending from './widgets/StudentsRequestsPending'
import PieChart from '../admin/widgets/PieChart'


import CourseList from '../usables/CourseList';



const useStyles = makeStyles(theme => ({
    content          : {
        '& canvas': {
            maxHeight: '100%'
        }
    },
    selectedProject  : {
        background  : theme.palette.primary.main,
        color       : theme.palette.primary.contrastText,
        borderRadius: '8px 0 0 0'
    },
    projectMenuButton: {
        background  : theme.palette.primary.main,
        color       : theme.palette.primary.contrastText,
        borderRadius: '0 8px 0 0',
        marginLeft  : 1
    },
}));


export default function AdminPage(props)
{
    const classes = useStyles(props);
    const pageLayout = useRef(null);
    const [selectedTab, setSelectedTab]=useState(0);


    const fcolumns = [
        {
          Header    : "SlNo",
          accessor  : "SlNo",
          className : "font-bold justify-center",
        },
        {
          Header    : "Name",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "Course",
        },
        {
          Header    : "Department",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "Department",

        },
        {
          Header    : "Email",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "Instructor",

        },    

        {
          Header    : "Position",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "Slots",

        },
        {
          Header    : "Office",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "Status",

        },

      ];

    function handleTabChange(event, tabValue)
    {  
        setSelectedTab(tabValue);

    }
    return (
        <FusePageSimple
        classes={{
            header      : "min-h-160 h-160",
            toolbar     : "min-h-48 h-48",
            rightSidebar: "w-288",
            content     : classes.content,
        }}
        header={
            <div className="flex flex-col justify-between flex-1 px-24 pt-24">
                <div className="flex justify-between items-start">
                    <Typography className="py-0 sm:py-24" variant="h4">Hello Dr. Auluck</Typography>
                    <Hidden lgUp>
                            <IconButton
                                onClick={(ev) => pageLayout.current.toggleRightSidebar()}
                                aria-label="open left sidebar"
                            >
                                <Icon>menu</Icon>
                            </IconButton>
                    </Hidden> 
                </div> {/*for the name written */}
            </div>
        }

        contentToolbar={
            <Tabs
                value={selectedTab}
                onChange={handleTabChange}
                indicatorColor="secondary"
                textColor="secondary"
                variant="scrollable"
                scrollButtons="off"
                className="w-full border-b-1 px-24"
            >
                <Tab className="text-14 font-600 normal-case" label="Home"/>
                <Tab className="text-14 font-600 normal-case" label="Course Analytics"/>
                <Tab className="text-14 font-600 normal-case" label="Enrolments"/>
                <Tab className="text-14 font-600 normal-case" label="Faculties"/>

            </Tabs>  
        }

        content={
            <div className="p-12">
                {selectedTab === 0 &&
                (
                    <FuseAnimateGroup
                        className="flex flex-wrap"
                        enter={{
                            animation: "transition.slideUpBigIn"
                        }}
                    >
                        {/* coruse , enroll , request */}
                    <p>Hello</p> 


                    </FuseAnimateGroup>
                )}

                {selectedTab === 1 && (
                   <FuseAnimateGroup
                        className="flex flex-wrap"
                        enter={{
                            animation: "transition.slideUpBigIn"
                        }}
                    >
                            <div className="widget flex w-full sm:w-1 md:w-1/3 p-12">
                                <CoursesAvailabe count={25}/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/3 p-12">
                                <Offerings count={25}/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/3 p-12">
                                <PendingRequests count={25}/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Floated Coarses"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Courses Offerings"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Pending Requests"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Rejected Requests"/>
                            </div>

                    </FuseAnimateGroup>

                )}

                {selectedTab === 2 && (
                   <FuseAnimateGroup
                        className="flex flex-wrap"
                        enter={{
                            animation: "transition.slideUpBigIn"
                        }}
                    >
                            <div className="widget flex w-full sm:w-1/2 md:w-1/4 p-12">
                                <CoursesEnrolling count={25} label="Courses for offering"/>
                            </div>
                            <div className="widget flex w-full sm:w-1/2 md:w-1/4 p-12">
                                <StudentsRequestsApproved count={25} label="Offering Proposals"/>
                            </div>
                            <div className="widget flex w-full sm:w-1/2 md:w-1/4 p-12">
                                <StudentsRequestPending count={25} label="HOD Approvals"/>
                            </div>
                            <div className="widget flex w-full sm:w-1/2 md:w-1/4 p-12">
                                <StudentsRequestsRejected count={25} label="Dean Approvals"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Courses Enrolling"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Students Enrolled"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Pending Requests"/>
                            </div>
                            <div className="widget flex w-full sm:w-1 md:w-1/2 p-12">
                                <PieChart title="Rejected Requests"/>
                            </div>

                    </FuseAnimateGroup>

                )}
                {selectedTab === 3 &&
                (
                    <FuseAnimateGroup
                        className="flex flex-wrap"
                        enter={{
                            animation: "transition.slideUpBigIn"
                        }}
                    >
                        {/* coruse , enroll , request */}
                        <CourseList columns = {fcolumns} ></CourseList>

                    </FuseAnimateGroup>
                )}
            </div>   
        }

        rightSidebarContent={
            <FuseAnimateGroup
                className="w-full"
                enter={{
                    animation: "transition.slideUpBigIn"
                }}
            >
                <div className="widget w-full p-12">
                    <Clock/>
                </div>
            </FuseAnimateGroup>
        }
        >

        </FusePageSimple>
    );
}
