import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import {FuseAnimateGroup} from '@fuse';
import {Toolbar, AppBar } from '@material-ui/core';
import GradeCard from './GradeCard'
import Chip from '@material-ui/core/Chip';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  toolbar: {
    minHeight: 128,
    alignItems: 'flex-start',
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    alignSelf: 'flex-end',
  },
  chip:{
    color: "green",
  }
}));

export default function SemCard(props) {
  console.log(props, "props");
  const classes = useStyles();

  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  function sgpa()
  {

  }
  return (
    <div className="md:flex max-w-2xl">
            <div className="flex flex-col flex-1 md:pr-32">
                <FuseAnimateGroup
                    enter={{
                        animation: "transition.slideUpBigIn"
                    }}
                >
              <Card className={classes.root}>
                <AppBar position="static" elevation={0}>
                    <Toolbar className="pl-16 pr-8">
                        <Typography variant="subtitle1" color="inherit" className="flex-1">
                           Semester-{props.item[0].sem} , Session- {props.item[0].session}
                        </Typography>

                    </Toolbar>
                </AppBar>

                <CardContent >
                  <Chip label="Registered Credits : 24" className={classes.chip} variant="outlined" />
                  <Chip label="Earned Credits : 24" className={classes.chip} variant="outlined" />
                  <Chip label="SGPA : 10" className={classes.chip} variant="outlined" />
                  <Chip label="CGPA : 9.5" className={classes.chip} variant="outlined" />
                </CardContent>

                <CardActions disableSpacing>
                  <IconButton
                    className={clsx(classes.expand, {
                      [classes.expandOpen]: expanded,
                    })}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more"
                  >
                    <ExpandMoreIcon />
                    <Typography>Details</Typography>
                  </IconButton>
                </CardActions>
                <Collapse in={expanded} timeout="auto" unmountOnExit>
                  <CardContent>
                    <GradeCard item = {props.item} />
                  </CardContent>
                </Collapse>
              </Card>

            </FuseAnimateGroup>
            </div>
            <br></br>
        </div>
  );
}
