import React, { useEffect, useState } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputBase from '@material-ui/core/InputBase';
import { useDispatch, useSelector } from 'react-redux';
import axiosInstance from 'app/main/axios';

import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';

import CourseList from '../usables/CourseList';
import { Typography } from '@material-ui/core';
import OpenMenu from '../usables/openMenu';
import OfferCourse from './OfferCourse';

import CourseOfferingForm from './CourseInstructor';
import {Link} from 'react-router-dom';

const BootstrapInput = withStyles((theme) => ({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #ced4da',
    fontSize: 16,
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  select: {
    width: 100,
  },
  button: {
    width: 100,
  },
  heading: {
    textAlign: 'center',
    fontSize: 20,


  }
}));

export default function CourseOfferingPage() {
  const classes = useStyles();
  const [status, setStatus] = React.useState('');
  const [department, setDepartment] = React.useState([{
    'id':0,
    'collegeId':'',
    'departmentName':'All',
  }])
  const [searchData,setSearchData] = React.useState({
    department:0,
    courseName:'',
    session:'',
    credit:'',
    status:'',
    courseCode:'',
    instructor:'',
  });
  const [dataList,setDataList]=React.useState([])
  const [deptValue,setDeptValue]=React.useState(null);
  const [status_value,setStatusValue]=React.useState(null);

  const [courseData,setCourseData]=React.useState(null);
  const user = useSelector(({ auth }) => auth.user);



  const columns = [
    {
      Header    : "SlNo",
      accessor  : "SlNo",
      className : "font-bold justify-center",
    },
    {
      Header    : "Course",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "Course",
    },
    {
      Header    : "Department",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "Department",

    },
    {
      Header    : "Instructor",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "Instructor",

    },    

    {
      Header    : "Slots",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "Slots",

    },
    {
      Header    : "Status",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "Status",

    },
    {
      Header   : "Action",
      accessor : "",
      Cell     : row => {
          return (
            <Link style={{color:"green" }}    to ={{pathname: "/academy/crp/CourseOffer" , courseName : row.original.Course ,dept :row.original.Department  }} >Offer</Link>
            // <CourseOfferingForm row = {row} />
            // <Button style={{color:"green"}} variant= "outlined" onClick ={() => offerCourse(row)} >Offer</Button>
          )
      },
      className: "justify-center",
  },
  ];
  useEffect(() => {
    if (user != undefined) {
      console.log(user.info[0].collegeId_id);
      setSearchData({...searchData,'collegeId':user.info[0].collegeId_id});
      axiosInstance.get('get-college-info/', {
        params: {
          collegeId: user.info[0].collegeId_id
        }
      }
      ).then(
        (response) => {
          const list=response.data.concat(...department)
          setDepartment(list);
        }
      );
    }
  }, [user]);


  const handleStatusChange = (event) => {
    setStatusValue(event.target.value);

    setSearchData({...searchData,'status':event.target.value});
  };
  const handleCourseCodeChange =(event) => {
    setSearchData({...searchData,'courseCode':event.target.value});
  }
  const handleSearch = () => {
    axiosInstance.get('search-courses/',{
      params: searchData
    }).then(
      (response) => {
        console.log(response.data);
        const list=[]
        for(var i=0;i<response.data.length;i++){
          var value = response.data[i];
          list.push(
            { 'SlNo' : i+1 ,
              'CourseId':value.id,
              'Course' : `${value.courseCode} :: ${value.courseName}`,
              'Department':value.department__departmentName,
              'Instructor':`${value.creatorId__first_name} ${value.creatorId__last_name}`,
              'Slots':"HSS or Program Core"  ,
              'Status':value.status 
            }
          )
        }

        setDataList(list);
        setCourseData(response.data);
      }
    )
  }
  const handleDepartmentChange = (event) =>
  {
      setDeptValue(event.target.value);
      setSearchData({...searchData, 'department': event.target.value})
  }
  const handleTitleChange = (event) => {
    setSearchData({...searchData,'courseName':event.target.value});
  }
  const handleInstructorNameChange = (event) => {
    setSearchData({...searchData,'instructor':event.target.value});
  }
  // const handleSessionChange = (event) => {
  //   setSearchData({...searchData,'session':event.target.value});
  // }
  // const handleCreditChange = (event) => {
  //   setSearchData({...searchData,'credit':event.target.value});
  // }

  return (

    <div>

      <Grid container spacing={3}>
        <Grid item xs={12} md={1} lg={1}>

        </Grid>
        <Grid item xs={12} md={10} lg={10}>
          <Paper className={classes.paper}>
            <br></br>

            <Typography className={classes.heading}>Find Offered Courses</Typography>
            <List>
              <ListItem>
                <FormControl className={classes.margin}>

                  <TextField
                    id="department"
                    select
                    label="Department"
                    // className={classes.select}

                    onChange={handleDepartmentChange}
                    helperText="Please select Department"
                    value={deptValue==null?0:deptValue}
                  >
