import React, { useEffect, useMemo, useState } from 'react';
import CourseList from '../usables/CourseList'
import Checkbox from '@material-ui/core/Checkbox';

import ProgressBar from '../usables/progressbar';

import {
    Button,
    Card,
    CardContent,
    OutlinedInput,
    Icon,
    TextField,
    Typography,
    CardActions,
    Divider,
    Select,
    InputLabel,
    FormControl,
    MenuItem,
    LinearProgress,
    Container,
    ListItem
} from '@material-ui/core';
import { makeStyles, useTheme } from '@material-ui/styles';
import { FuseAnimate, FuseAnimateGroup } from '@fuse';
import clsx from 'clsx';
import _ from '@lodash';

import SheetUpload from './SheetUpload';



const useStyles = makeStyles(theme => ({
    header: {
        background: 'linear-gradient(to right, ' + theme.palette.primary.dark + ' 0%, ' + theme.palette.primary.main + ' 100%)',
        color: theme.palette.getContrastText(theme.palette.primary.main)
    },
    headerIcon: {
        position: 'absolute',
        top: -64,
        left: 0,
        opacity: .04,
        fontSize: 512,
        width: 512,
        height: 512,
        pointerEvents: 'none'
    }
}));

function GmailService() {

    const classes = useStyles();

    const [columns, setColumns] = React.useState(null);
    const [rows, setRows] = React.useState(null);
    const [subject, setSubject] = React.useState(null);
    const handleChange = (event) => {
        setSubject(event.target.value);
    }
    const [list,setCheckList]=React.useState([]);

    const handleUpdate=(e) =>{
        // setColumns(e.target.value)
    };



    return (
        <div className="flex flex-col flex-1 w-full">


            <div
                className={clsx(classes.header, "relative overflow-hidden flex flex-col flex-shrink-0 items-center justify-center text-center p-16 sm:p-24 h-200 sm:h-288")}>

                <FuseAnimate animation="transition.slideUpIn" duration={400} delay={100}>
                    <Typography color="inherit" className="text-24 sm:text-40 font-light">
                        Upload Your File Here
                    </Typography>

                </FuseAnimate>

                <SheetUpload color="inherit" row={rows} columns={columns} subject={subject} list={list} setColumns={setColumns.bind(this)} setRows={setRows.bind(this)} />


                <Icon className={classes.headerIcon}>school</Icon>
            </div>
            <FuseAnimateGroup
                enter={{
                    animation: "transition.slideUpBigIn"
                }}
                className="flex flex-wrap py-24"
            >

                <Container>

                    <ProgressBar />

                    <TextField
                        id="Subject"
                        label="Subject"
                        helperText="Please select Subject of Email"
                        onChange={handleChange}
                    ></TextField>
                    {rows != null && columns != null ?
                        <CourseList columns={columns} data={rows} />
                        : null}
                    <ListItem>
                    {rows != null && columns != null ?
                        columns.map((value) => {
                            if (value.Header != 'email')
                                return (
                                    <div>
                                        <Checkbox
                                            name={value.Header}
                                            onChange={handleUpdate}
                                        />
                                        <label>{value.Header}</label>
                                    </div>
                                );
                            else
                                return null;
                        })
                        : null}
                    </ListItem>

                </Container>

            </FuseAnimateGroup>
        </div>
    );
}

export default GmailService;
