import React, { useEffect, useState } from 'react';
import { Avatar, Button, Tab, Tabs, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { FusePageSimple, FuseAnimate, FuseNavigation, FuseDialog } from '@fuse';
import People from './tabs/People';
import Timeline from './tabs/Timeline';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import IconButton from '@material-ui/core/IconButton';
import ShareForm from './tabs/ShareForm';
import Grid from '@material-ui/core/Grid';
import { useDispatch, useSelector } from 'react-redux';
import withReducer from 'app/store/withReducer';
import reducer from '../store/reducers';
import * as Actions from '../store/actions';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import TextField from '@material-ui/core/TextField';
import Divider from '@material-ui/core/Divider';

const useStyles = makeStyles(theme => ({
    layoutHeader: {
        height: 320,
        minHeight: 320,
        [theme.breakpoints.down('md')]: {
            height: 240,
            minHeight: 240
        }
    },
    tabs: {
        borderRight: `1px solid ${theme.palette.divider}`,
    }
    ,
    header: {
        backgroundColor: "blue",
    }
}));


function StudentCoursePage(props) {

    const dispatch = useDispatch();
    const classes = useStyles();
    const course = useSelector(({academyApp}) => academyApp.course);
    const user = useSelector(({ auth }) => auth.user);

    const [courseData,setCourseData]=useState({});
    // console.log(props.match.params);

    useEffect(() => {

    dispatch(Actions.getTagList(props.match.params));

    },[dispatch,props.match.params]);

useEffect(() => {
    /**
     * Get the Course Data
     */
    dispatch(Actions.getCoursePostList(props.match.params));
}, [dispatch, props.match.params]);


useEffect(() => {
    /**
     * Get the Course Name
     */
    setCourseData({...courseData,'name':props.match.params.courseName});
}, [props]);



    const [selectedTab, setSelectedTab] = useState(0);
    const [selectedTag, setSelectedTag] = useState(0);
    const [selectedType, setSelectedType] = useState("all");

    const [dialogOpen, setDiaglogOpen]=useState(false);
    var tagName;

    function handleTabChange(event, value) {
        setSelectedTab(value);
    }

    function handleTagClicked(val , val1)
    {
        // alert(val);

        setSelectedTag(val);
        setSelectedType(val1);

    }

    function handleTypeClicked(val)
    {
        // alert(val);

        setSelectedType(val);

    }

    function handleTextChange(event)
    {
        tagName=event.target.value;
    }
    function handleOpen()
    {
        setDiaglogOpen(true);
    }
    function handleClose()
    {
        setDiaglogOpen(false);
    }
    function createTag()
    {
        dispatch(Actions.createTag(props.match.params, tagName));
        handleClose();
    }

    return (
        <div>
            <div className=" p-24 flex flex-1 flex-col items-center justify-center md:flex-row md:items-end">
                <div className="flex flex-1 flex-col items-center justify-center md:flex-row md:items-center md:justify-start">
                    <FuseAnimate animation="transition.expandIn" delay={300}>
                        <Avatar className="w-96 h-96" src="assets/images/avatars/Velazquez.jpg" />
                    </FuseAnimate>
                    <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                        <Typography className="md:ml-24" variant="h4" color="inherit"> {courseData.name}</Typography>
                    </FuseAnimate>
                </div>
                <div className="flex items-center justify-end">
                    <ShareForm data={props.match.params}/>
                    {/* <Button className="normal-case" variant="contained" color="primary" aria-label="Send Message">Send Message</Button> */}
                </div>
            </div>

            <FusePageSimple
                contentToolbar={
                    <Tabs
                        value={selectedTab}
                        onChange={handleTabChange}
                        indicatorColor="secondary"
                        textColor="secondary"
                        variant="scrollable"
                        scrollButtons="off"
                        classes={{
                            root: "h-64 w-full border-b-1"
                        }}
                    >
                        <Tab
                            classes={{
                                root: "h-64"
                            }}
                            label="Timeline" />


                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="People" />
                    </Tabs>
                }
                content={

                    <div className="p-16 sm:p-24">
                        {selectedTab === 0 &&
                            (

                                <Timeline type ={selectedType} tag= {selectedTag} posts={course == null ? null : course.postList} />
                            )}
                        {selectedTab === 1 && (
                            <People />
                        )}

                </div>

            }



            leftSidebarContent={
                <div>
                    <List dense className={classes.root}>
                    <ListItem key={0} onClick = {()=>handleTypeClicked("all")}  button>

                            <ListItemText  primary= "All" />

                     </ListItem>
                     <ListItem key={0} onClick = {()=>handleTypeClicked("exams")}  button>

                            <ListItemText  primary= "Exams" />

                     </ListItem> <ListItem key={0} onClick = {()=>handleTypeClicked("assignments")}  button>

                            <ListItemText  primary= "Assignments" />

                     </ListItem> <ListItem key={0} onClick = {()=>handleTypeClicked("material")}  button>

                            <ListItemText  primary= "Material" />

                     </ListItem>
                    <br></br>
                    <Divider/>

                    <Typography className="md:ml-24" variant="h5"  color="inherit"> Topics </Typography>

                    <br>
                    </br>
                    {course== null? [] : course.tagList.map((value) => {
                        return (
                            <div>
                        <ListItem key={value.id} onClick = {()=>handleTagClicked(value.id , "topic")}  button>

                            <ListItemText  primary= {value.tag} />

                        </ListItem>
                        {/* <Divider> */}
                        </div>
                        );
                    })}
                    </List>

                    {
                    //  user.isStaff ? 
                     <IconButton onClick={handleOpen}>
                        <AddCircleIcon />
                     </IconButton>

                    //  :null

                    }
                     <Dialog open={dialogOpen} onClose={handleClose}>
                        <DialogContent color="primary">
                            <TextField onChange={handleTextChange}>
                            </TextField>
                            </DialogContent>
                        <DialogActions>
                            <Button onClick={handleClose} color="secondary">
                                Cancel
                            </Button>
                            <Button onClick={createTag} color="secondary">
                                Create Tag
                            </Button>
                        </DialogActions>
                    </Dialog>

                </div>  
            }
            ></FusePageSimple>
        </div>
    )
}

export default withReducer('academyApp', reducer)(StudentCoursePage);
