import axiosInstance from 'app/main/axios';
export const GET_COURSES = '[ACADEMY APP] GET COURSES';


export function getCourses()
{
    const request = axiosInstance.get('my-courses/');
    return (dispatch) =>
        request.then((response) =>
            dispatch({
                type   : GET_COURSES,
                payload: response.data
            })
        );
}
