import React, { useEffect, useMemo, useState } from 'react';
import CourseList from '../usables/CourseList'
import Checkbox from '@material-ui/core/Checkbox';
import Upload from '../usables/Upload';
import Chip from '@material-ui/core/Chip';

import {
    Button,
    Card,
    CardContent,
    OutlinedInput,
    Icon,
    TextField,
    Typography,
    CardActions,
    Divider,
    Select,
    InputLabel,
    FormControl,
    MenuItem,
    LinearProgress,
    Container,
    ListItem
} from '@material-ui/core';
import { makeStyles, useTheme } from '@material-ui/styles';
import { FuseAnimate, FuseAnimateGroup } from '@fuse';
import clsx from 'clsx';
import _ from '@lodash';




const useStyles = makeStyles(theme => ({
    header: {
        background: 'linear-gradient(to right, ' + theme.palette.primary.dark + ' 0%, ' + theme.palette.primary.main + ' 100%)',
        color: theme.palette.getContrastText(theme.palette.primary.main)
    },
    headerIcon: {
        position: 'absolute',
        top: -64,
        left: 0,
        opacity: .04,
        fontSize: 512,
        width: 512,
        height: 512,
        pointerEvents: 'none'
    }
}));

function AdminHomeTab() {

    const classes = useStyles();

    const [columns, setColumns] = React.useState(null);
    const [rows, setRows] = React.useState(null);
    const [subject, setSubject] = React.useState(null);
    const handleChange = (event) => {
        setSubject(event.target.value);
    }
    const [list, setList] = React.useState([]);
    const handleUpdate = (event) => {
        if (event.target.checked == true) {
            console.log(event.target.name);
            setList(() => [...list, event.target.name]);
        }
        else {
            var ls = [];
            for (var i in list) {
                if (list[i] == event.target.name)
                    continue;
                ls.push(list[i]);
            }
            console.log(ls);
            setList(ls);
        }
    }
    return (
        <div className="flex flex-col flex-1 w-full">


            <div
                className={"relative overflow-hidden flex flex-col flex-shrink-0 items-center justify-center text-center p-16 sm:p-24 h-200 sm:h-288"}>

                <FuseAnimate animation="transition.slideUpIn" duration={400} delay={100}>
                    <Typography color="inherit" className="text-24 sm:text-40 font-light">
                        Upload Your File Here
                    </Typography>

                </FuseAnimate>
                <FuseAnimate animation="transition.slideUpIn" duration={400} delay={100}>
                    <Typography color="inherit" className="text-18 sm:text-20 font-light">
                        The Uploaded .csv file should have the following columns only :-
                    <br></br>
                        <ListItem>

                            {['courseCode', 'courseName', 'department', 'credit', 'creatorName'].map((value) => (
                                <div className={'mx-auto'}>
                                    <Chip variant="outlined" size="medium" label={value} color="secondary" />
                                </div>
                            ))}
                        </ListItem>

                    </Typography>

                </FuseAnimate>

                <Upload color="inherit" row={rows} columns={columns} subject={subject} list={list} setColumns={setColumns.bind(this)} setRows={setRows.bind(this)} />


            </div>
            <FuseAnimateGroup
                enter={{
                    animation: "transition.slideUpBigIn"
                }}
                className="flex flex-wrap py-24"
            >

                <Container>
                    {rows != null && columns != null ?
                        <CourseList columns={columns} data={rows} />
                        : null}



                </Container>

            </FuseAnimateGroup>
        </div>
    );
}

export default AdminHomeTab;
