import React, { useEffect, useState } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputBase from '@material-ui/core/InputBase';
import { useDispatch, useSelector } from 'react-redux';
import axiosInstance from 'app/main/axios';

import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';

import CourseList from '../usables/CourseList';
import { Typography } from '@material-ui/core';
import {Link, Redirect} from 'react-router-dom';
import * as tableGrid from 'gridjs-react';

const BootstrapInput = withStyles((theme) => ({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #ced4da',
    fontSize: 16,
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  select: {
    width: 100,
  },
  button: {
    width: 100,
  },
  heading: {
    textAlign: 'center',
    fontSize: 20,


  }
}));

export default function CourseOffered() {
  const classes = useStyles();
  const [status, setStatus] = React.useState('');
  const [department, setDepartment] = React.useState([{
    'id': 0,
    'collegeId': '',
    'departmentName': 'All',
  }])
  const [searchData, setSearchData] = React.useState({
    department: 0,
    courseName: '',
    session: '',
    credit: '',
    status: '',
    courseCode: '',
    session: ''
  });
  const [deptValue, setDeptValue] = React.useState(null);
  const [status_value,setStatusValue]=React.useState(null);

  const [courseData, setCourseData] = React.useState(null);
  const [dataList, setDataList] = React.useState(null);
  const user = useSelector(({ auth }) => auth.user);
  // ['S.no', 'Course', 'Department', 'Instructor', 'Academic Session', 'Enrol. Count', 'Slots', 'Status','More Info']
  const columns = [
    {
      Header    : "SlNo",
      accessor  : "sno",
      sortable  :true,
      className : "font-bold justify-center",
    },
    {
      Header    : "Course",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "course",

    },
    {
      Header    : "Department",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "department",

    },
    {
      Header    : "Instructor",
      filterable: true,
      sortable:true,
      className : "font-bold  justify-center",
      accessor  : "instructor",

    },  
    {
      Header    : "Session",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "session",

    },    

    {
      Header    : "Enrol. Count",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "enrolCnt",

    },
    {
      Header    : "Slots",
      filterable: true,
      sortable:true,
      className : "font-bold justify-centerf",
      accessor  : "slots",

    },
    {
      Header    : "Status",
      filterable: true,
      sortable:true,
      className : "font-bold justify-center",
      accessor  : "status",

    },
    {
      Header   : "Action",
      accessor : "",
      Cell     : row => {
          return (
            // <Link style={{color:"green" }}    to ={{pathname: "/academy/crp/CourseOffer" ,id:row.original.CourseId , courseName : row.original.Course ,dept :row.original.Department  }} >Offer</Link>
            // <CourseOfferingForm row = {row} />
            // <Button style={{color:"green"}} variant= "outlined" onClick ={() => offerCourse(row)} >Offer</Button>
            <Button
            to={`/academy/crp/courseDetails/${1}`}
            component={Link}
            className="justify-start px-32"
            color="secondary"
            varia
        >
            View Details
            </Button>

          )
      },
      className: "justify-center",
  },

  ];

  useEffect(() => {
    if (user != undefined) {
      console.log(user.info[0].collegeId_id);
      setSearchData({ ...searchData, 'collegeId': user.info[0].collegeId_id });
      axiosInstance.get('get-college-info/', {
        params: {
          collegeId: user.info[0].collegeId_id
        }
      }
      ).then(
        (response) => {
          const list = response.data.concat(...department)
          setDepartment(list);
        }
      );
    }
  }, [user]);


  const handleStatusChange = (event) => {
    setStatusValue(event.target.value);
    setSearchData({ ...searchData, 'status': event.target.value });
  };
  const handleCourseCodeChange = (event) => {
    setSearchData({ ...searchData, 'courseCode': event.target.value });
  }
  const handleSearch = () => {
    axiosInstance.get('search-course-offering/', {
      params: searchData
    }).then(
      (response) => {
        console.log(response.data);
        const list = []
        for (var i = 0; i < response.data.length; i++) {
          var value = response.data[i];
          list.push(
            {'sno':`${i + 1}`,
             'course':`${value.courseId__courseCode} :: ${value.courseId__courseName}`,
             'department':value.department__departmentName,
            'instructor':`${value.coordinatorId__userId__first_name} ${value.coordinatorId__userId__last_name}`,
           'session': `${value.session} Sem- ${value.sem}`,
             'enrolCnt': '67',
             'slot': "HSS or Program Core",
             'status': value.status,
            }
          )
        }
        setDataList(list);
      }
    )
  }
  const handleDepartmentChange = (event) => {
    setDeptValue(event.target.value);
    setSearchData({ ...searchData, 'department': event.target.value })
  }
  const handleTitleChange = (event) => {
    setSearchData({ ...searchData, 'courseName': event.target.value });
  }
  // const handleInstructorNameChange = (event) => {
  //   setSearchData({...searchData,'instructor':event.target.value});
  // }
  const handleSessionChange = (event) => {
    setSearchData({ ...searchData, 'session': event.target.value });
  }
  const handleCreditChange = (event) => {
    setSearchData({ ...searchData, 'credit': event.target.value });
  }

  return (

    <div>

      <Grid container spacing={3}>
        <Grid item xs={12} md={1} lg={1}>

        </Grid>
        <Grid item xs={12} md={10} lg={10}>
          <Paper style={{backgroundColor:"#FAE5D3"}}>
            <br></br>

            <Typography className={classes.heading}>Find Offered Courses</Typography>
            <List>
              <ListItem>
                <FormControl className={classes.margin}>

                  <TextField
                    id="department"
                    select
                    label="Department"
                    // className={classes.select}

                    onChange={handleDepartmentChange}
                    helperText="Please select Department"
                    value={deptValue == null ? 0 : deptValue}
                  >
                    {department == undefined ? <em>none</em> :
                      department.map((value) => (
                        <MenuItem value={value.id} >
                          <em>{value.departmentName}</em>
                        </MenuItem>
                      ))
                    }
                  </TextField>
                </FormControl>
                <FormControl className={classes.margin}>

                  <TextField
                    autoFocus
                    margin="dense"
                    id="Code"
                    label="Course Code"
                    type="text"
                    fullWidth
                    variant="outlined"
                    onChange={handleCourseCodeChange}
                  />
                </FormControl>
                <FormControl className={classes.margin}>

                  <TextField
                    autoFocus
                    margin="dense"
                    id="title"
                    label="Title"
                    type="text"
                    fullWidth
                    variant="outlined"
                    onChange={handleTitleChange}
                  />
                </FormControl>
                <FormControl className={classes.margin}>

                  <TextField
                    autoFocus
                    margin="dense"
                    id="session"
                    label="Session"
                    type="text"
                    fullWidth
                    variant="outlined"
                    onChange={handleSessionChange}

                  />
                </FormControl>

                <FormControl className={classes.margin}>

                  <TextField
                    autoFocus
                    margin="dense"
                    id="credits"
                    label="credits"
                    type="text"
                    fullWidth
                    variant="outlined"
                    onChange={handleCreditChange}
                  />
                </FormControl>
                <FormControl className={classes.margin}>

                  <TextField
                    autoFocus
                    margin="dense"
                    id="instructor"
                    label="InstructorName"
                    type="text"
                    fullWidth
                    variant="outlined"
                  />
                </FormControl>

                <FormControl variant="outlined" className={classes.margin}>
                  <TextField
                    id="standard-select-currency"
                    select
                    label="status"
                    // className={classes.select}
                    onChange={handleStatusChange}
                    helperText="Choose Status"
                    value={status_value}
                  >

                    <MenuItem key="" value=''>None</MenuItem>
                    <MenuItem key="" value="Draft">Draft</MenuItem>
                    <MenuItem key="" value="Approved">Approved</MenuItem>
                    <MenuItem key="" value="Pending HoD Approval">Pending HoD Approval</MenuItem>
                    <MenuItem key="" value="HoD Rejected">HoD Rejected</MenuItem>
                    <MenuItem key="" value="Council Approval Pending">Council Approval Pending</MenuItem>
                    <MenuItem key="" value="Council Rejected">Council Rejected</MenuItem>
                    <MenuItem key="" value="Retired">Retired</MenuItem>
                  </TextField>
                </FormControl>

                <Button onClick={handleSearch} variant="outlined" style={{ color: "green" }}>
                  Search
                  </Button>

                <Button variant="outlined" style={{ color: "red" }}>
                  Clear
                    </Button>
              </ListItem>
            </List>
          </Paper>
          <br>
          </br>
          {/* <CourseTable /> */}

          {(dataList != null) ?
            <CourseList data={dataList} columns={columns} /> : null
          }

        </Grid>
        <Grid item xs={12} md={1} lg={1}>

        </Grid>


      </Grid>


    </div>
  );
