import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Link from '@material-ui/core/Link';
import axiosInstance from 'app/main/axios';

import { Icon, IconButton} from '@material-ui/core';


import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';


export default function WorkFormDialog() {
  const [open, setOpen] = React.useState(false);
  const [checkedstate ,setState] = React.useState(false);

  var data = {};
  data['jobTitle']= '';
  data['company']= '' ;
  data['jobLocation']= '' ;
  data['jobDescription']= '' ;
  data['startDate']= '' ;
  data['endDate']= '' ;


  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange =() =>{
    setState(!checkedstate);
  };


  const jobTitleHandler=(e) =>{
    data['jobTitle']= e.target.value ;
  }
  const companyHandler=(e) =>{
    data['company']= e.target.value ;
  }  
  const jobLocationHandler=(e) =>{
    data['jobLocation']= e.target.value ;
  }  
  const jobDescriptionHandler=(e) =>{
    data['jobDescription']= e.target.value ;
  }  
  const startDateHandler=(e) =>{
    data['startDate']= e.target.value ;
  }
  const endDateHandler=(e) =>{
    data['endDate']= e.target.value ;
  }



  const handleAddExperienceInfo = () => {


    axiosInstance.post('add-experience/', data = data )
    .then((res) => {

    })
    .catch((err) => {
        alert(err);
    });
    handleClose();

  };

  return (
    <div>

      <IconButton onClick={handleClickOpen} aria-label="display more actions" edge="end" color="inherit">
        <Icon>edit</Icon>
     </IconButton>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Edit credentials</DialogTitle>
        <DialogContent>
          <DialogContentText>
           Add employment credentials
          </DialogContentText>

          <TextField
            autoFocus
            margin="dense"
            id="jobTitle"
            label="Job Title"
            type="text"
            fullWidth
            variant="outlined"
            onChange={jobTitleHandler}
          />

        <TextField
            autoFocus
            margin="dense"
            id= "company"
            label="Company/Organisation"
            type="text"
            fullWidth
            variant="outlined"
            onChange={companyHandler}
          />


        <TextField
            autoFocus
            margin="dense"
            id= "jobLocation"
            label="Job Location"
            type="text"
            fullWidth
            variant="outlined"
            onChange={jobLocationHandler}
          />


        <TextField
            autoFocus
            margin="dense"
            id= "jobDescription"
            label="Job Description"
            type="text"
            fullWidth
            variant="outlined"
            onChange={jobDescriptionHandler}

          />


      <TextField
        autoFocus
        margin="dense"

        id="startDate"
        label="Start Date"
        type="date"
        fullWidth
        variant="outlined"      
        InputLabelProps={{
          shrink: true,
        }}
        onChange ={startDateHandler}
      />

<TextField
        autoFocus
        margin="dense"

        id="endDate"
        label="End Date"
        type="date"
        fullWidth
        variant="outlined"      
        InputLabelProps={{
          shrink: true,
        }}
        onChange ={endDateHandler}

      />
      {/* <FormControlLabel

     control  = { <Checkbox
            checked={checkedstate}
            onChange={handleChange}
            name="checked"
            color="primary"

          />}
        label = "I currently work here"
        labelPlacement="start"

          /> */}

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleAddExperienceInfo} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
