import React, { useEffect, useState } from 'react';
import { Avatar, Button, Tab, Tabs, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { FusePageSimple, FuseAnimate } from '@fuse';
import TimelineTab from './tabs/TimelineTab';
import PhotosVideosTab from './tabs/PhotosVideosTab';
import PostsTab from './tabs/PostsTab';
import QuestionsTab from './tabs/QuestionsTab';
import AnswersTab from './tabs/AnswersTab';
import FollowersTab from './tabs/FollowersTab';
import FollowingTab from './tabs/FollowingTab';
import AboutTab from './tabs/AboutTab'
import axiosInstance from '../axios';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import { Editor } from '@tinymce/tinymce-react';
import store from 'app/store';
import jwtService from 'app/services/jwtService/jwtService'
import { Redirect } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';

const useStyles = makeStyles(theme => ({
    layoutHeader: {
        height: 320,
        minHeight: 320,
        [theme.breakpoints.down('md')]: {
            height: 240,
            minHeight: 240
        }
    }
}));





function ProfilePage() {
    const classes = useStyles();
    const [selectedTab, setSelectedTab] = useState(0);
    const [profiledata, setProfiledata] = useState([]);
    const [profilefetched, setprofilefetched] = useState(false);
    const user = useSelector(({ auth }) => auth.user);
    console.log(user);



    function handleTabChange(event, value) {
        setSelectedTab(value);
    }
    var body;
    const [dialogopen, setOpen] = React.useState(false);

    const openPostForm = () => {
        setOpen(true);
    }

    const handleClose = () => {
        setOpen(false);
    }

    const handleEditorChange = (content, event) => {
        body = content;
    }

    const Post = () => {
        axiosInstance
            .post('post-create/', {
                body: body,
            })
            .then((res) => {
                alert("post done");
            })
            .catch((err) => {
                alert(err)
            });

        setOpen(false);
    }
    if (jwtService.isAuthTokenValid())
        return (

            <FusePageSimple
                classes={{
                    header: classes.layoutHeader,
                    toolbar: "px-16 sm:px-24"
                }}
                header={
                    <div className="p-24 flex flex-1 flex-col items-center justify-center md:flex-row md:items-end">
                        <div className="flex flex-1 flex-col items-center justify-center md:flex-row md:items-center md:justify-start">
                            <FuseAnimate animation="transition.expandIn" delay={300}>
                                <Avatar className="w-96 h-96" src="assets/images/avatars/Velazquez.jpg" />
                            </FuseAnimate>
                            <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                <Typography className="md:ml-24" variant="h4" color="inherit">{`${user.data.firstName}  ${user.data.lastName}`}</Typography>
                            </FuseAnimate>
                        </div>

                        <div className="flex items-center justify-end">
                            <Button onClick={openPostForm} className="mr-8 normal-case" variant="contained" color="primary" aria-label="Follow">Post</Button>
                            <Button className="mr-8 normal-case" variant="contained" color="secondary" aria-label="Follow">Follow</Button>
                            <Button className="normal-case" variant="contained" color="primary" aria-label="Send Message">Send Message</Button>
                        </div>
                        <div>
                            <Dialog open={dialogopen}>
                                <DialogContent>
                                    <Editor

                                        initialValue="<p>This is the initial content of the editor</p>"
                                        init={{
                                            height: 500,
                                            menubar: true,
                                            plugins: [
                                                'advlist autolink lists link image charmap print preview anchor',
                                                'searchreplace visualblocks code fullscreen',
                                                'insertdatetime media table paste code help wordcount'
                                            ],
                                            toolbar:
                                                'undo redo | formatselect | bold italic backcolor | \
                                        alignleft aligncenter alignright alignjustify | \
                                        bullist numlist outdent indent | removeformat | help'
                                        }}
                                        onEditorChange={handleEditorChange}
                                    />
                                </DialogContent>
                                <DialogActions>
                                    <Button onClick={handleClose} color="primary">
                                        Cancel
                                </Button>

                                    <Button onClick={Post} color="primary">
                                        Post
                                </Button>
                                </DialogActions>
                            </Dialog>
                        </div>
                    </div>
                }
                contentToolbar={
                    <Tabs
                        value={selectedTab}
                        onChange={handleTabChange}
                        indicatorColor="secondary"
                        textColor="secondary"
                        variant="scrollable"
                        scrollButtons="off"
                        classes={{
                            root: "h-64 w-full border-b-1"
                        }}
                    >
                        <Tab
                            classes={{
                                root: "h-64"
                            }}
                            label="TimeLine" />
                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="About" />
                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="Posts" />
                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="Answers" />
                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="Questions" />
                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="Followers" />
                        <Tab
                            classes={{
                                root: "h-64"
                            }} label="Following" />
                    </Tabs>
                }
                content={
                    <div className="p-16 sm:p-24">
                        {selectedTab === 0 &&
                            (
                                <TimelineTab />
                            )}
                        {selectedTab === 1 && (
                            <AboutTab profiledata={profiledata} />
                        )}
                        {selectedTab === 2 && (
                            <PhotosVideosTab />
                        )}
                        {selectedTab === 2 && (
                            <PostsTab />
                        )}
                        {selectedTab === 3 && (
                            <AnswersTab />
                        )}
                        {selectedTab === 4 && (

                            <QuestionsTab />
                        )}
                        {selectedTab === 5 && (
                            <FollowersTab />
                        )}
                        {selectedTab === 6 && (
                            <FollowingTab />
                        )}
                    </div>
                }
            />
        )
    else
        return (<Redirect to='/login' />)
}

export default ProfilePage;
