import React, {useEffect, useRef, useState} from 'react';
import {Icon, Typography, Paper, IconButton} from '@material-ui/core';
import moment from 'moment';

export default function Clock()
{
    const [time, setTime] = useState(moment());
    const intervalRef = useRef();

    useEffect(() => {
        intervalRef.current =
            setInterval(update, 1000);
        return () => {
            clearInterval(intervalRef.current);
        };
    });

    function update()
    {
        setTime(moment());
    }

    return (
        <Paper className="w-full rounded-12 shadow-none border-1">
            <div className="flex items-center justify-between pr-4 pl-16 pt-4">
                <Typography className="text-16 text-blue text-center text-bold" align='center' >
                    {time.format('dddd, HH:mm:ss')}
                </Typography>

            </div>
            <div className="text-center px-24 py-32">
                <Typography className="text-24 leading-tight text-green" color='secondary'>
                    {time.format('MMMM')}
                </Typography>
                <Typography className="text-72 leading-tight text-orange" >
                    {time.format('D')}
                </Typography>
                <Typography className="text-24 leading-tight text-green" color="secondary">
                    {time.format('Y')}
                </Typography>
            </div>
        </Paper>
    );
}
