import * as Actions from '../actions';

const initialState = null;

const courseReducer = function (state = initialState, action) {
    switch ( action.type )
    {
        case Actions.GET_COURSE:
        {
            return {
                ...state,
                'postList':action.payload
            };
        }
        case Actions.ADD_POST:
        {
            return{
                ...state,
                'postList':state.postList.concat(action.payload)
            }
        }
        case Actions.GET_TAGS:
        {
            return {
                ...state,
                'tagList':action.payload
            };
        }
        case Actions.SAVE_COURSE:
        {
            return {
                ...action.payload
            };
        }
        case Actions.UPDATE_COURSE:
        {
            return {
                ...state,
                ...action.payload
            };
        }
        case Actions.CREATE_TAG:
            {
                return {
                    ...state,
                    'tagList':state.tagList.concat(action.payload)
                };
            }
        case Actions.GET_CLASS_COMMENTS:
            {
                state.postList[action.payload.serialNum]['comments']=action.payload.comments
                return {
                    ...state 
                }
            }
        default:
        {
            return state;
        }
    }
};

export default courseReducer;
