import {Grid, Paper , TextField , Avatar, AppBar, Button, Card, CardContent, Icon, IconButton, List, ListItem, ListItemSecondaryAction, ListItemText, Toolbar, Typography} from '@material-ui/core';
import {FuseAnimateGroup} from '@fuse';

import Container from '@material-ui/core/Container';



import React, { useEffect, useState, useRef } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import InputBase from '@material-ui/core/InputBase';
import { useDispatch, useSelector } from 'react-redux';
import axiosInstance from 'app/main/axios';

import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import DeleteIcon from '@material-ui/icons/Delete';
import CourseList from '../usables/CourseList';
import { Chip, Divider } from '@material-ui/core';
import * as tableGrid from 'gridjs-react';

import {Link} from 'react-router-dom';

import CourseOfferingDialog from '../usables/courseOfferingDialog';


const BootstrapInput = withStyles((theme) => ({
  root: {
    'label + &': {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #ced4da',
    fontSize: 16,
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:focus': {
      borderRadius: 4,
      borderColor: '#80bdff',
      boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
    },
  },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
    width: "97%",
  },
 color: {
   color:"red",
 },
 chip: {
  margin: theme.spacing(0.5),
  color:"blue",

},
  select: {
    width: 100,
  },
  button: {
    width: 100,
  },
  heading: {
    textAlign: 'center',
    fontSize: 20,
  }
}));


function CourseOfferingForm(props)
{


  console.log( props.location.courseName)

  const classes = useStyles();
  const [department, setDepartment] = React.useState([{
    'id': 0,
    'collegeId': '',
    'departmentName': 'All',
  }])
  const [searchData, setSearchData] = React.useState({
    department: 0,
    courseName: '',
    session: '',
    credit: '',
    status: '',
    courseCode: '',
  });
  const [deptValue, setDeptValue] = React.useState(null);
  const [course, setCourseValue] = React.useState(null);
  const [status, setStatusValue] = React.useState(null);
  const [section, setSectionValue] = React.useState(null);
  const [slot, setSlotValue] = React.useState(null);
  const [session,setSessionValue] = React.useState(null);
  const [instructor , setInst] = React.useState('');
  const [coordinator , setCoordinator] = React.useState('');
  const [instructorList, setInstructor] = React.useState([]);
  const [creditList ,setCreditList] = React.useState([]);
