import React, { useEffect, useState } from 'react';
import { Avatar, Button, Tab, Tabs, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { FusePageSimple, FuseAnimate } from '@fuse';
import jwtService from 'app/services/jwtService/jwtService'
import { Redirect } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import StudentProfileTab from './tabs/StudentProfileTab';
import EnrolmentPage from './tabs/EnrollmentTab';
import Feedback from './tabs/FeedbackTab';
import Help from './tabs/HelpTab';
import TeacherProfileTab from './tabs/TeacherProfileTab';
import CourseOffered from './tabs/CourseOffered';
import CourseOfferingPage from './tabs/CourseOfferingPage';
import PendingWork from './tabs/PendingWork/PendingWork';
import ProposeCourse from './tabs/ProposeCourse';
import CourseInstructor from './tabs/CourseInstructor.js';
import OfferCourse from './tabs/OfferCourse';
import Chip from '@material-ui/core/Chip';
const useStyles = makeStyles(theme => ({
    layoutHeader: {
        height: 200,
        minHeight: 200,
        [theme.breakpoints.down('md')]: {
            height: 240,
            minHeight: 240
        }
    }
}));





function CourseRegistrationPortal() {
    const classes = useStyles();
    const [selectedTab, setSelectedTab] = useState(0);

    const user = useSelector(({ auth }) => auth.user);
    console.log(user);



    function handleTabChange(event, value) {
        setSelectedTab(value);
    }

    if (jwtService.isAuthTokenValid())
        return (

            <FusePageSimple
                classes={{
                    header: classes.layoutHeader,
                    toolbar: "px-16 sm:px-24"
                }}
                header={
                    <div  className="p-24 flex flex-1 flex-col">
                        <div  className="flex flex-1 flex-col items-left justify-content-between">
                            <div className="flex flex-1 flex-col items-center justify-center md:flex-row md:items-center md:justify-start">
                                <FuseAnimate animation="transition.expandIn" delay={300}>
                                    <Avatar className="w-96 h-96" src="assets/images/avatars/Velazquez.jpg" />
                                </FuseAnimate>
                                <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                    <Typography className="font-bold mb-4 text-21 md:ml-24" variant="h4" color="inherit">{`${user.data.firstName}  ${user.data.lastName}`}</Typography>
                                </FuseAnimate>
                            </div>
                            <div  className="flex flex-1 flex-col items-center justify-center md:flex-row md:items-center md:justify-start">
                                {/* { => ( */}
                                <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                    {/* <Typography className="font-bold mb-4 text-17 md:ml-24" variant="body1" color="inherit">{value}:</Typography> */}
                                    <Chip label={`Roll No : 2018csb1201`} color="inherit" variant="outlined" />

                                </FuseAnimate>

                                <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                    {/* <Typography className="font-bold mb-4 text-17 md:ml-24" variant="body1" color="inherit">{value}:</Typography> */}
                                    <Chip label={`Email Address : ${user.data.email}`} color="inherit" variant="outlined" />

                                </FuseAnimate>

                                <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                    {/* <Typography className="font-bold mb-4 text-17 md:ml-24" variant="body1" color="inherit">{value}:</Typography> */}
                                    <Chip label={`Degree : B-Tech`} color="inherit" variant="outlined" />

                                </FuseAnimate>

                                <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                    {/* <Typography className="font-bold mb-4 text-17 md:ml-24" variant="body1" color="inherit">{value}:</Typography> */}
                                    <Chip label={`Department : Computer Science and Engineering`} color="inherit" variant="outlined" />

                                </FuseAnimate>

                                <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                                    {/* <Typography className="font-bold mb-4 text-17 md:ml-24" variant="body1" color="inherit">{value}:</Typography> */}
                                    <Chip label={`Year of Entry : 2018 `} color="inherit" variant="outlined" />

                                </FuseAnimate>
                                {/* ))} */}
                            </div>
                        </div>
                    </div>
                }
                contentToolbar={

                    user.data.isStaff ?

                        <Tabs
                            value={selectedTab}
                            onChange={handleTabChange}
                            indicatorColor="secondary"
                            textColor="secondary"
                            variant="scrollable"
                            scrollButtons="off"
                            classes={{
                                root: "h-64 w-full border-b-1"
                            }}
                        >
                            <Tab
                                classes={{
                                    root: "h-64"
                                }}
                                label="Teacher Record" />
                            <Tab
                                classes={{
                                    root: "h-64"
                                }}
                                label="Propose Course" />

                            <Tab
                                classes={{
                                    root: "h-64"
                                }} label="Courses Available For Offering" />
                            <Tab
                                classes={{
                                    root: "h-64"
                                }} label="Courses Offered" />
                            <Tab
                                classes={{
                                    root: "h-64"
                                }} label="Pending Work" />
                            {/* <Tab
                                classes={{
                                    root: "h-64"
                                }} label="courseOffering" /> */}
                            {/* <Tab
                                classes={{
                                    root: "h-64"
                                }} label="OfferCourse" /> */}
                        </Tabs>
                        :
                        <Tabs
                            value={selectedTab}
                            onChange={handleTabChange}
                            indicatorColor="secondary"
                            textColor="secondary"
                            variant="scrollable"
                            scrollButtons="off"
                            classes={{
                                root: "h-64 w-full border-b-1"
                            }}
                        >

                            <Tab
                                classes={{
                                    root: "h-64"
                                }}
                                label="Student Record" />
                            <Tab
                                classes={{
                                    root: "h-64"
                                }} label="Enrolment Portal" />
                            <Tab
                                classes={{
                                    root: "h-64"
                                }} label="FeedBack" />
                            <Tab
                                classes={{
                                    root: "h-64"
                                }} label="Help" />

                        </Tabs>
                }
                content={
                    // <StudentProfileTab />

                    user.data.isStaff ?
                        <div className="p-16 sm:p-24">
                            {selectedTab === 0 &&
                                (

                                    <TeacherProfileTab />
                                )}

                            {selectedTab === 1 && (
                                <ProposeCourse />
                            )}

                            {selectedTab === 2 && (
                                <CourseOfferingPage />
                            )}
                            {selectedTab === 3 && (
                                <CourseOffered />
                            )}
                            {selectedTab === 4 && (
                                <PendingWork />
                            )}
                            {/* {selectedTab === 5 && (
                                <CourseInstructor />
                            )} */}
                                {/* {selectedTab === 6 && (
                                <OfferCourse />
                            )} */}
                        </div>

                        :
                        <div className="p-16 sm:p-24">
                            {selectedTab === 0 &&
                                (

                                    <StudentProfileTab />
                                )}
                            {selectedTab === 1 && (
                                <EnrolmentPage />
                            )}
                            {selectedTab === 2 && (
                                <Feedback />
                            )}
                            {selectedTab === 3 && (
                                <Help />
                            )}

                        </div>

                }
            />
        )
    else
        return (<Redirect to='/login' />)
}

export default CourseRegistrationPortal;
