import zIndex from '@material-ui/core/styles/zIndex';
import { useDispatch, useSelector } from 'react-redux';

export default function PostsTab(props)
{
    const course = useSelector(({ academyApp }) => academyApp.course);
    if(!props.posts)
    return null;
    else
    console.log(props);

    // alert(props.tag)

    return(
        <div>

            {
                {
                "all": props.posts.map(index => ( <CoursePostCard item={index}></CoursePostCard> )).reverse(),
                "material": props.posts.filter(idx => idx.postType == props.type).map(index => ( <CoursePostCard item={index}></CoursePostCard> )).reverse(),
                "exams": props.posts.filter(idx => idx.postType == props.type).map(index => ( <CoursePostCard item={index}></CoursePostCard> )).reverse(),
                "assignments": props.posts.filter(idx => idx.postType == props.type).map(index => ( <CoursePostCard item={index}></CoursePostCard> )).reverse(),
                "topic": props.posts.filter(idx => idx.tag == props.tag).map(index =>( <CoursePostCard item={index}></CoursePostCard> )).reverse() 

                }[props.type]
            }




        </div>

    )
}
