import React from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Link from '@material-ui/core/Link';
import axiosInstance from 'app/main/axios';
import {Avatar, AppBar, Button, Card, CardContent, Icon, IconButton, List, ListItem, ListItemSecondaryAction, ListItemText, Toolbar, Typography} from '@material-ui/core';
import FormControl from '@material-ui/core/FormControl';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import DeleteIcon from '@material-ui/icons/Delete';


const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
    width: "97%",
  },

    chip: {
      margin: theme.spacing(0.5),
      color:"blue",

    },

 color: {
   color:"red",
 },
  select: {
    width: 100,
  },
  button: {
    width: 100,
  },
  heading: {
    textAlign: 'center',
    fontSize: 20,
  }
}));


export default function EducationFormDialog(props) {

  const [open, setOpen] = React.useState(false);
  const classes = useStyles();
  const [degree , setDegreeValue] = React.useState(null);
  const [deptValue , setDeptValue]= React.useState(null);
  const [category , setCategoryValue] = React.useState(null);
  const [year, setYear] = React.useState(null);
  var index = 0;

  // const [credit_list, setCreditCategory] = React.useState([]);


  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const addCategory = () => {
     index++;
    let l = {
      'index': index,
      'degree':degree,
      'dept':deptValue,
      'category':category,
      'year':year
    };

    props.setCreditCategory(credit_list => [...credit_list, l]);

    handleClose();
  };

  const handleRemoveCategory = (e) => {
    const i = e.target.getAttribute("name")
    setCreditCategory(credit_list.filter(item => item.index !== i));
   };

  const handleDegreeChange = (event) => {
    setDegreeValue(event.target.value);
  };
  const handleDepartmentChange = (event) => {
    setDeptValue(event.target.value);
  };
  const handleCategoryChange = (event) => {
    setCategoryValue(event.target.value);
  };

  const handleYearChange = (event) => {
    setYear(event.target.value);

  }




  return (

<Card className="w-full mb-16">
                        <AppBar position="static" elevation={0}>
                            <Toolbar className="pl-16 pr-8">
                                <Typography variant="subtitle1" color="inherit" className="flex-1">
                                    Crediting Categorisation
                                </Typography>

                                {/* <CourseOfferingDialog department ={department} credit_list = {credit_list} setCredit={setCreditCategory.bind(this)}/> */}

                                <Button style = {{color :"inherit"}} onClick= {handleClickOpen}> Add </Button>

                                <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                                  <DialogTitle id="form-dialog-title">Edit credentials</DialogTitle>
                                  <DialogContent >
                                    <DialogContentText>
                                      Add Crediting Categorisation
                                    </DialogContentText>
                                    <FormControl variant="outlined" className ={classes.margin} >
                                            <TextField
                                              id="standard-select-currency"
                                              select
                                              label="Degree"
                                              // className={classes.select}
                                              variant="outlined"
                                              onChange ={handleDegreeChange}
                                              value= {degree}
                                            >

                                              <MenuItem key="" value="B-Tech"> B-Tech </MenuItem>
                                              <MenuItem key="" value="B-Tech Dual">B-Tech Dual</MenuItem>
                                              <MenuItem key="" value="M-Tech">M-Tech</MenuItem>
                                              <MenuItem key="" value="Phd">Phd</MenuItem>

                                            </TextField>
                                          </FormControl>
                                          <FormControl variant="outlined" className ={classes.margin} >
                                            <TextField
                                              id="standard-select-currency"
                                              select
                                              label="Department"
                                              // className={classes.select}
                                              onChange={handleDepartmentChange}
                                              variant = "outlined"
                                              value = {deptValue}
                                            >

                                          {props.department == undefined ? <em>none</em> :
                                                          props.department.map((value) => (
                                                            <MenuItem value={value.departmentName} >
                                                              <em>{value.departmentName}</em>
                                                            </MenuItem>
                                                          ))}

                                            </TextField>
                                          </FormControl>
                                          <FormControl variant="outlined" className ={classes.margin}>
                                            <TextField
                                              id="standard-select-currency"
                                              select
                                              label="Category"
                                              // className={classes.select}
                                              onChange={handleCategoryChange}
                                              value = {category}
                                              helperText="Choose Course Category"
                                            >

                                              <MenuItem key="" value="Programme Core">Programme Core</MenuItem>
                                              <MenuItem key="" value="Programme Elective">Programme Elective</MenuItem>
                                              <MenuItem key="" value="Open Elective">Open Elective</MenuItem>

                                              <MenuItem key="" value="Science Maths Core">Science Maths Core</MenuItem>
                                              <MenuItem key="" value="Science Maths Elective">Science Maths Elective</MenuItem>
                                              <MenuItem key="" value="HSS Core">HSS Core</MenuItem>
                                              <MenuItem key="" value="HSS Elective">HSS Elective</MenuItem>
                                            </TextField>
                                          </FormControl>

                                          <FormControl className ={classes.margin} >

                                            <TextField
                                              autoFocus
                                              margin="dense"
                                              id="instructor"
                                              label="For Entry Year"
                                              type="text"
                                              fullWidth
                                              variant="outlined" 
                                              onChange={handleYearChange}

                                            />
                                          </FormControl>

                                  </DialogContent>
                                  <DialogActions>
                                    <Button onClick={handleClose} color="primary">
                                      Cancel
                                    </Button>
                                    <Button onClick={addCategory}  color="primary">
                                        Save
                                    </Button>
                                  </DialogActions>
                                </Dialog>
                            </Toolbar>
                        </AppBar>
                        <CardContent>
                          <List>
                            {
                            credit_list.map((index) => {
                              return (
                               <ListItem >
                                <Chip label={index.degree} className={classes.chip}/>
                                <Chip label={index.dept} className={classes.chip}/>
                                <Chip label={index.category} className={classes.chip}/>
                                <Chip label={index.year} className={classes.chip}/>
                                <ListItemSecondaryAction>
                                     <IconButton>
                                       <DeleteIcon></DeleteIcon>
                                     </IconButton>
                                   </ListItemSecondaryAction>
                               </ListItem>

                              );
                              })}
                        </List>
                   </CardContent>
  </Card>

  );
}
