import React, {useState, useEffect} from 'react';
import {makeStyles} from '@material-ui/styles';
import { useDispatch, useSelector } from 'react-redux';

import {
    AppBar,
    Avatar,
    Button,
    Card,
    CardActions,
    CardContent,
    CardHeader,
    Divider,
    Icon,
    IconButton,
    Input,
    List,
    ListItem,
    ListItemText,
    Paper,
    Toolbar,
    Typography
} from '@material-ui/core';

import axiosInstance from 'app/main/axios';
import Grid from '@material-ui/core/Grid';
import Collapse from '@material-ui/core/Collapse';
import PostComment from './PostComment';
import CommentCard from './CommentCard';

const useStyles = makeStyles(theme => ({
    layoutHeader: {
        height                        : 200,
        minHeight                     : 200,
        [theme.breakpoints.down('md')]: {
            height   : 140,
            minHeight: 140
        }
    }
    ,
    card: {

        minHeight                     : 200,
    }
}));



export  default function CoursePostCard(props)
{

    const classes = useStyles();


  const [commentData, setCommentData] = React.useState([])
  const [comment, setComment] = React.useState(false);

  function commentFetcher() 
  {

    const promise = axiosInstance.get('class-comments/' + props.item.id+"/");
    const dataPromise = promise.then((response) => response.data);
    return dataPromise;
  }

  function temp() {
      commentFetcher().then(data => {
      setCommentData(data)
      setComment(true)

    })
  }

const showComments = () => 
{

  if (comment == 0)
    temp();
    else 
    {
        setComment(!comment);
    }
};

    return(
    <div>
        <Card key={props.item.id} className= {classes.card + " mb-32 overflow-hidden"}>
            <CardHeader
                avatar={
                    <Avatar aria-label="Recipe" alt={props.item.creatorId__first_name[0].toUpperCase()}/>
                }
                // this is 3 dots in upperRight
                action={
                    <IconButton aria-label="more">
                        <Icon>more_vert</Icon>
                    </IconButton>
                }
                title={props.item.title}
                subheader={props.item.creatorId__first_name}
            />

            <CardContent className="py-0">
                { <div><p>{props.item.body}</p>
                 {props.item.Tag__Tag}
                 gdhsfh
                </div>}
            </CardContent>

            <CardActions disableSpacing>
                <Button aria-label="Favorite">
                    <Icon className="text-16 mr-8" color="action">comment</Icon>
                </Button>
            </CardActions>

            <AppBar className="card-footer flex flex-column p-16" position="static" color="default" elevation={0}>
                <div className="flex items-center">
                    <Button onClick={showComments}>
                         comments
                        <Icon className="text-16 ml-4" color="action">keyboard_arrow_down</Icon>

                    </Button>
                </div>

                <Collapse in={comment} timeout="auto" unmountOnExit>

                    <PostComment comment={comment} setComment={setComment.bind(this)} postId={props.item.id} parentCommentId={null} />

                    {commentData.length > 0 ?  <List >{commentData.map((index)=> (<CommentCard item = {index}  />))}</List>:null}
                 </Collapse>
            </AppBar>


        </Card>
    </div>
                            );
}
