import React from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Link from '@material-ui/core/Link';
import axiosInstance from 'app/main/axios';
import {Avatar, AppBar, Button, Card, CardContent, Icon, IconButton, List, ListItem, ListItemSecondaryAction, ListItemText, Toolbar, Typography} from '@material-ui/core';


export default function GeneralInfoFormDialog() {
  const [open, setOpen] = React.useState(false);

  var data = {};
  data['about'] ='';
  data['gender']= '' ;
  data['signatureLine']= '' ;
  data['birthday'] = '' ;
  data['designation']= '';



  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const aboutHandler=(e) =>{
    data['about']= e.target.value ;
  }
  const genderHandler=(e) =>{
    data['gender']= e.target.value ;
  }  
  const signatureHandler=(e) =>{
    data['signatureLine']= e.target.value ;
  }  
  const dateHandler=(e) =>{
    data['birthday']= e.target.value ;
  }  
  const designationHandler=(e) =>{
    data['designation']= e.target.value ;
  }
  const handleAddGeneralInfo = () => {


    axiosInstance.post('add-info/', data = data )
    .then((res) => {

    })
    .catch((err) => {
        alert(err);
    });
    handleClose();

  };

  return (
    <div>


      <IconButton onClick={handleClickOpen} aria-label="display more actions" edge="end" color="inherit">
        <Icon>edit</Icon>
     </IconButton>


      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Edit credentials</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Add General credential
          </DialogContentText>

            <TextField
                autoFocus
                margin="dense"

                id="about"
                label="About Me"
                type="text"
                fullWidth
                variant="outlined"   
                onChange={aboutHandler}

            />

        <TextField
                autoFocus
                margin="dense"

                id="signatureLine"
                label="Signature Line"
                type="text"
                fullWidth
                variant="outlined"   
                onChange={signatureHandler}
            />


          <TextField
            autoFocus
            margin="dense"
            id ="gender"
            label="Gender"
            type="text"
            fullWidth
            variant="outlined"
            onChange={genderHandler}
          />

        <TextField
            autoFocus
            margin="dense"
            id= "birthday"
            label="Date of Birth"
            type="date"
            fullWidth
            variant="outlined"
            onChange={dateHandler}
            InputLabelProps={{
                shrink: true,
                }}
          />

      <TextField
            autoFocus
            margin="dense"
            id="designation"
            label="Designation"
            type="text"
            fullWidth
            variant="outlined"
            onChange={designationHandler}
          />

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleAddGeneralInfo} color="primary">
          Save

          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
