import React, { useEffect, useMemo, useState } from 'react';
import history from '@history';
import CourseList from '../usables/CourseList';

import {
  Button,
  Card,
  CardContent,
  OutlinedInput,
  Icon,
  TextField,
  Typography,
  CardActions,
  Divider,
  Select,
  InputLabel,
  FormControl,
  MenuItem,
  LinearProgress
} from '@material-ui/core';
import { makeStyles, useTheme } from '@material-ui/styles';
import { FuseAnimate, FuseAnimateGroup } from '@fuse';
import { useDispatch, useSelector } from 'react-redux';
import withReducer from 'app/store/withReducer';
import clsx from 'clsx';
import _ from '@lodash';
import { Link } from 'react-router-dom';


const useStyles = makeStyles(theme => ({
  header: {
    background: 'linear-gradient(to right, ' + theme.palette.primary.dark + ' 0%, ' + theme.palette.primary.main + ' 100%)',
    color: theme.palette.getContrastText(theme.palette.primary.main)
  },
  headerIcon: {
    position: 'absolute',
    top: -64,
    left: 0,
    opacity: .04,
    fontSize: 512,
    width: 512,
    height: 512,
    pointerEvents: 'none'
  }
}));

export default function TeacherProfileTab(props) {
  const classes = useStyles(props);
  const theme = useTheme();
  const coursesData = [1, 2];
  const columns = [
    {
      Header: "SlNo",
      accessor: "SlNo",
      className: "font-bold justify-center",
    },
    {
      Header: "CourseCode",
      filterable: true,
      sortable: true,
      className: "font-bold justify-center",
      accessor: "courseCode",
    },
    {
      Header: "Department",
      filterable: true,
      sortable: true,
      className: "font-bold justify-center",
      accessor: "Department",

    },
    {
      Header: "Status",
      filterable: true,
      sortable: true,
      className: "font-bold justify-center",
      accessor: "Status",

    },
    // {
    //   Header: "Action",
    //   accessor: "",
    //   Cell: row => {
    //     return (
    //       <Button style={{ color: "green" }} variant="outlined">Offer</Button>
    //     )
    //   },
    //   className: "justify-center",
    // },
  ];
  const data=[
    {
      'SlNo':1,
      'courseCode':'CS304',
      'Department':'CSE',
      'Status': 'Proposed'
    },
    {
      'SlNo':2,
      'courseCode':'CS305',
      'Department':'CSE',
      'Status': 'Pending HOD Approval'
    },
    {
      'SlNo':3,
      'courseCode':'EE201',
      'Department':'EE',
      'Status': 'Proposed'
    },
  ]




  return (
    <div>
      <Typography variant="h4" align="center" className={classes.custom}>
        Current Applications
    </Typography>
      {<CourseList data={data} columns={columns} />}
      <Typography variant="h4" align="center" className={classes.custom}>
        Ongoing Courses
      </Typography>
      <div className="flex flex-col flex-1 max-w-2xl w-full mx-auto px-8 sm:px-16 py-24">

        {useMemo(() => (
          coursesData && (
            (coursesData.length > 0) ? (
              <FuseAnimateGroup
                enter={{
                  animation: "transition.slideUpBigIn"
                }}
                className="flex flex-wrap py-24"
              >
                {coursesData.map((course) => {
                  return (
                    <div className="w-full pb-24 sm:w-1/2 lg:w-1/3 sm:p-16">
                      <Card elevation={1} className="flex flex-col h-256">
                        <div
                          className="flex flex-shrink-0 items-center justify-between px-24 h-64"
                          style={{
                            background: '#8BC34A',
                            color: '#000000'
                          }}
                        >
                          <Typography className="font-medium truncate" color="inherit">2-0-2-5-3 (credits 3)</Typography>
                          <div className="flex items-center justify-center opacity-75">
                            <Icon className="text-20 mr-8" color="inherit">access_time</Icon>
                            <div className="text-16 whitespace-no-wrap">Running</div>
                          </div>
                        </div>
                        <CardContent className="flex flex-col flex-auto items-center justify-center">
                          <Typography className="text-center text-16 font-400">CS301</Typography>
                          <Typography className="text-center text-13 font-600 mt-4" color="textSecondary">2020-21 Sem-1</Typography>
                        </CardContent>
                        <Divider />
                        <CardActions className="justify-center">
                          <Button
                            component={Link}
                            className="justify-start px-32"
                            color="secondary"
                            onClick={() => {
                              history.push('crp/coursedetails/1/');;
                            }}
                          >See More
                          </Button>
                        </CardActions>
                        <LinearProgress
                          className="w-full"
                          variant="determinate"
                          color="secondary"
                          value={30}
                        />
                      </Card>
                    </div>
                  )
                })}
              </FuseAnimateGroup>
            ) :
              (
                <div className="flex flex-1 items-center justify-center">
                  <Typography color="textSecondary" className="text-24 my-24">
                    No courses found!
                                    </Typography>
                </div>
              )
          )), [coursesData, theme.palette])}
      </div>
      <Typography variant="h4" align="center" className={classes.custom}>
        Past Offerings
      </Typography>
      <div className="flex flex-col flex-1 max-w-2xl w-full mx-auto px-8 sm:px-16 py-24">

        {useMemo(() => (
          coursesData && (
            (coursesData.length > 0) ? (
              <FuseAnimateGroup
                enter={{
                  animation: "transition.slideUpBigIn"
                }}
                className="flex flex-wrap py-24"
              >
                {coursesData.map((course) => {
                  return (
                    <div className="w-full pb-24 sm:w-1/2 lg:w-1/3 sm:p-16">
                      <Card elevation={1} className="flex flex-col h-256">
                        <div
                          className="flex flex-shrink-0 items-center justify-between px-24 h-64"
                          style={{
                            background: '#FFB74D',
                            color: '#000000'
                          }}
                        >
                          <Typography className="font-medium truncate" color="inherit">2-0-2-5-3 (credits 3)</Typography>
                          <div className="flex items-center justify-center opacity-75">
                            <Icon className="text-20 mr-8" color="inherit">access_time</Icon>
                            <div className="text-16 whitespace-no-wrap">Completed</div>
                          </div>
                        </div>
                        <CardContent className="flex flex-col flex-auto items-center justify-center">
                          <Typography className="text-center text-16 font-400">CS301</Typography>
                          <Typography className="text-center text-13 font-600 mt-4" color="textSecondary">2020-21 Sem-1</Typography>
                        </CardContent>
                        <Divider />
                        <CardActions className="justify-center">
                          <Button
                            component={Link}
                            className="justify-start px-32"
                            color="secondary"
                            onClick={() => {
                              history.push('crp/pastOfferingDetails/1/');
                            }}
                          >See More
                                            </Button>
                        </CardActions>
                        <LinearProgress
                          className="w-full"
                          variant="determinate"
                          color="secondary"
                        />
                      </Card>
                    </div>
                  )
                })}
              </FuseAnimateGroup>
            ) :
              (
                <div className="flex flex-1 items-center justify-center">
                  <Typography color="textSecondary" className="text-24 my-24">
                    No courses found!
                                    </Typography>
                </div>
              )
          )), [coursesData, theme.palette])}

      </div>

    </div>
  );
}
