import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Link from '@material-ui/core/Link';

import { Icon, IconButton} from '@material-ui/core';
import axiosInstance from 'app/main/axios';


import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';


export default function ContactFormDialog() {
  const [open, setOpen] = React.useState(false);

  var data = {};
  data['address']= '';
  data['email']= '' ;
  data['website']= '' ;
  data['phone']= '' ;



  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  const addressHandler=(e) =>{
    data['address']= e.target.value ;
  }
  const phoneHandler=(e) =>{
    data['phone']= e.target.value ;
  }  
  const websiteHandler=(e) =>{
    data['website']= e.target.value ;
  }  
  const emailHandler=(e) =>{
    data['email']= e.target.value ;
  }  

  const handleAddContactInfo = () => {


    axiosInstance.post('add-contact/', data = data )
    .then((res) => {

    })
    .catch((err) => {
        alert(err);
    });

    handleClose();

  };



  return (
    <div>

      <IconButton onClick={handleClickOpen} aria-label="display more actions" edge="end" color="inherit">
        <Icon>edit</Icon>
     </IconButton>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Edit credentials</DialogTitle>
        <DialogContent>
          <DialogContentText>
         Add contact credentials
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="address"
            label="Address"
            type="text"
            fullWidth
            variant="outlined"
            onChange = {addressHandler}
          />

        <TextField
            autoFocus
            margin="dense"
            id= "phone"
            label="Phone/Teliphone No"
            type="text"
            fullWidth
            variant="outlined"
            onChange = {phoneHandler}
          />


        <TextField
            autoFocus
            margin="dense"
            id= "website"
            label="Personal Website"
            type="text"
            fullWidth
            variant="outlined"
            onChange = {websiteHandler}
          />


        <TextField
            autoFocus
            margin="dense"
            id= "email"
            label="Email"
            type="text"
            fullWidth
            variant="outlined"
            onChange = {emailHandler}
          />



        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleAddContactInfo} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
