import React from 'react';
import Input from '@material-ui/core/Input';
import Button from '@material-ui/core/Button';
import axiosInstance from 'app/main/axios';
import {Paper , Avatar} from '@material-ui/core';

export default function PostComment(props)
{
    let text;
  const handleTextOnChange=(event)=>
  {
    text=event.target.value;

  }


  function submitComment()
  {
    axiosInstance.post('comment-create/', 
    {
      postId:props.postId, 
      parentCommentId:props.parentCommentId,
      body: text 
    }
    ).then((res) =>
    {

    }).catch((err) =>
    {
      alert(err)

    })
    props.setComment(!props.comment);
  }
  return (
    <div className="flex flex-auto">
        <Avatar src="assets/images/avatars/profile.jpg"/>
        <div className="flex-1 pl-8">
        <Paper elevation={0} className="w-full mb-16">
            <Input 
                onChange={handleTextOnChange}
                className="p-8 w-full border-1"
                classes={{root: 'text-13'}}
                placeholder="Add a comment.."
                multiline
                rows="1"
                margin="none"
                disableUnderline
            />
        </Paper>
        <Button onClick = {submitComment} className="normal-case" variant="contained" color="primary" size="small">Post Comment</Button>
        </div>
    </div>);
}
