import React from 'react';
import {
    AppBar,
    Avatar,
    Button,
    Icon,
    List,
    ListItem,
    ListItemText,
    Typography
} from '@material-ui/core';

import axiosInstance from 'app/main/axios';
import Grid from '@material-ui/core/Grid';
import Collapse from '@material-ui/core/Collapse';
import PostComment from './PostComment';



export default function CommentCard(props)
{
    const [commentData, setCommentData] = React.useState([])
    const [comment, setComment]=React.useState(false);

    function replyFetcher() {
        const promise = axiosInstance.get('reply-list/' + props.item.id);
        // using .then, create a new promise which extracts the data
        const dataPromise = promise.then((response) => response.data);
        // return it
        return dataPromise;
      }
      function temp() 
      {
          replyFetcher().then(data => {
          setCommentData(data)
          setComment(!comment)

        })
      }

      const replyClicked = () => 
      {

        if (comment == 0)
          temp();
        else {
          setComment(!comment);
        }

      };



    return(
    <div >  
            <ListItem className="px-0">
            <Avatar alt={props.item.username[0].toUpperCase()}  className="mr-16"/>
                <ListItemText
                    primary={(
                                <div>
                                    <Typography className="inline font-medium" color="initial" paragraph={false}>
                                        {props.item.username}
                                    </Typography>
                                    <Typography className="inline ml-4" variant="caption">
                                        {props.item.creation_date}
                                    </Typography>
                                </div>
                            )}
                        secondary={props.item.body} 
                    >
                </ListItemText>         
            </ListItem>
            <AppBar className="card-footer flex flex-column p-16" position="static" color="default" elevation={0}>
                    <div className="flex items-center">
                        <Button onClick={replyClicked}>
                            <Typography>Replies</Typography>
                            <Icon className="text-16 ml-4" color="action">keyboard_arrow_down</Icon>
                        </Button>
                    </div>

                    <Collapse in={comment} timeout="auto" unmountOnExit>
                        <Grid >
                            <PostComment comment={comment} setComment={setComment.bind(this)} postId={props.item.postId} parentCommentId={props.item.id} />
                            {commentData.length > 0 ?  <List >{commentData.map((index)=> (<CommentCard item = {index}  />))}</List>:null}
                        </Grid>
                    </Collapse>
            </AppBar> 

    </div>);
}
