import React, {useEffect, useRef, useState} from 'react';
import {FuseAnimateGroup, FusePageSimple} from '@fuse';
import {Menu, MenuItem, Hidden, Icon, IconButton, Tab, Tabs, Typography, Container} from '@material-ui/core';
import {makeStyles} from '@material-ui/styles';
import Clock from '../Clock'

import Offerings from './widgets/Offerings'
import PendingRequests from './widgets/PendingRequests'
import CoursesAvailabe from './widgets/CoursesAvailable'
import CoursesEnrolling from './widgets/CoursesEnrolling'
import StudentsRequestsRejected from './widgets/StudentsRequestRejected'
import StudentsRequestsApproved from './widgets/StudentsRequestsApproved'
import StudentsRequestPending from './widgets/StudentsRequestsPending'
import PieChart from '../admin/widgets/PieChart'
import Checkbox from '@material-ui/core/Checkbox';

import CourseList from '../usables/CourseList';



const useStyles = makeStyles(theme => ({
    content          : {
        '& canvas': {
            maxHeight: '100%'
        }
    },
    selectedProject  : {
        background  : theme.palette.primary.main,
        color       : theme.palette.primary.contrastText,
        borderRadius: '8px 0 0 0'
    },
    projectMenuButton: {
        background  : theme.palette.primary.main,
        color       : theme.palette.primary.contrastText,
        borderRadius: '0 8px 0 0',
        marginLeft  : 1
    },
}));


export default function AdminPage(props)
{
    const classes = useStyles(props);
    const pageLayout = useRef(null);
    const [selectedTab, setSelectedTab]=useState(0);


    const fcolumns = [
        {
          Header    : "SlNo",
          accessor  : "sno",
          className : "font-bold justify-center",
        },
        {
          Header    : "Name",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "name",
        },
        {
          Header    : "Department",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "dept",

        },
        {
          Header    : "Email",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "email",

        },    

        {
          Header    : "Position",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "position",

        },
        {
          Header    : "Office",
          filterable: true,
          sortable:true,
          className : "font-bold justify-center",
          accessor  : "office",

        },

      ];

      const fdata = [
          {
             'sno':1,
             'name':"Mr John" ,
             'dept':"CSE",
             'email':"john@iitrpr.ac.in",
             'position':"Assistant Prof",
            'office':"CE 303"
          },
          {
            'sno':1,
            'name':"Mr Alex" ,
            'dept':"CSE",
            'email':"alex@iitrpr.ac.in",
            'position':"Assistant Prof",
           'office':"CE 304"
          },
      ];





const request_data = [{
        'sno':1,
        'entry_no':"2018csb1201",
        'name':"Abhishek Kumar Gupta",
        'enrol_type':"credit",
        'grade':9.85,
        'dept':"ME",
        'extra':"None",
        'credits_completed':"130",
        'status':"approved",
    },
    {
        'sno':2,
        'entry_no':"2018csb1206",
        'name':"Mr John",
        'enrol_type':"credit",
        'grade':8.80,
        'dept':"EE",
        'extra':"Minor CSE",
        'credits_completed':"130",
        'status':"pending instructor approval",
    },
    {
        'sno':3,
        'entry_no':"2018csb1203",
        'name':"Mr John",
        'enrol_type':"credit",
        'grade':6.85,
        'dept':"ME",
        'extra':"Minor CSE",
        'credits_completed':"130",
        'status':"pending instructor approval",
    },
];

const request_columns=[
    {
        Header    : "Sl No",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "sno",
        className: "justify-center",

      },
      {
        Header    : "Entry No",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "entry_no",
        className: "justify-center",

      },
      {
        Header    : "Name",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "name",
        className: "justify-center",

      },    

      {
        Header    : "Enrolment Type",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "enrol_type",
        className: "justify-center",

      },  

    {
        Header    : "CGPA",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "grade",
        className: "justify-center",

      },           

      {
        Header    : "Department",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "dept",
        className: "justify-center",

      },   


      {
        Header    : "Specialisation",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "extra",
        className: "justify-center",

      },   

      {
        Header    : "Credits Completed",
        filterable: true,
        sortable:true,
        className : "font-bold",
        accessor  : "credits_completed",
        className: "justify-center",

      },   
      {
        Header    : "Status",
        filterable: true,
