import React from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Link from '@material-ui/core/Link';
import axiosInstance from 'app/main/axios';
import {Avatar, AppBar, Button, Card, CardContent, Icon, IconButton, List, ListItem, ListItemSecondaryAction, ListItemText, Toolbar, Typography} from '@material-ui/core';


export default function EducationFormDialog() {
  const [open, setOpen] = React.useState(false);

  var data = {};
  data['collegeName']= '' ;
  data['collegeId']= '' ;
  data['departmentId']= '' ;

  data['program']='' ;
  data['specialisation']= '' ;
  data['graduationYear']= '' ;

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  const collegeNameHandler=(e) =>{
    data['collegeName']= e.target.value ;
  }
  const collegeIdHandler=(e) =>{
    data['collegeId']= e.target.value ;
  }  
  const departmentIdHandler=(e) =>{
    data['departmentId']= e.target.value ;
  }  
  const programHandler=(e) =>{
    data['program']= e.target.value ;
  }  
  const specialisationHandler=(e) =>{
    data['specialisation']= e.target.value ;
  }
  const graduationDateHandler=(e) =>{
    data['graduationYear']= e.target.value ;
  }



  const handleAddEducationInfo = () => {


    axiosInstance.post('add-education/', data = data )
    .then((res) => {

    })
    .catch((err) => {
        alert(err);
    });

    handleClose();


  };

  return (
    <div>


      <IconButton onClick={handleClickOpen} aria-label="display more actions" edge="end" color="inherit">
        <Icon>edit</Icon>
     </IconButton>


      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Edit credentials</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Add education credential
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="collegeName"
            label="College Name"
            type="text"
            fullWidth
            variant="outlined"
            onChange={collegeNameHandler}

          />

      <TextField
            autoFocus
            margin="dense"
            id="collegeId"
            label="College Id"
            type="number"
            fullWidth
            variant="outlined"
            onChange={collegeIdHandler}
          />
        <TextField
          autoFocus
          margin="dense"
          id="departmentId"
          label="Department Id"
          type="number"
          fullWidth
          variant="outlined"
          onChange={departmentIdHandler}
        />
        <TextField
            autoFocus
            margin="dense"
            id= "program"
            label="Program"
            type="text"
            fullWidth
            variant="outlined"
            onChange={programHandler}
          />

      <TextField
            autoFocus
            margin="dense"
            id="specialisation"
            label="Specialisation"
            type="text"
            fullWidth
            variant="outlined"
            onChange={specialisationHandler}
          />
      <TextField
        autoFocus
        margin="dense"

        id="graduationYear"
        label="Graduation Year"
        type="date"
        fullWidth
        variant="outlined"      
        InputLabelProps={{
          shrink: true,
        }}
        onChange={graduationDateHandler}      />

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleAddEducationInfo} color="primary">
          Save

          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
