import React, {useEffect, useRef, useState} from 'react';
import {FuseAnimateGroup, FusePageSimple} from '@fuse';
import {Menu, MenuItem, Hidden, Icon, IconButton, Tab, Tabs, Typography} from '@material-ui/core';
import {makeStyles} from '@material-ui/styles';
import Clock from '../Clock'
import CoursesFloated from './widgets/CoursesFloated'
import Pending from './widgets/Pending'
import Offerings from './widgets/Offerings'
import Rejected from './widgets/Rejected'
import CoursesEnrolling from './widgets/CoursesEnroling'
import StudentsEnrolled from './widgets/StundentsEnrolled'
import PendingRequests from './widgets/PendingRequests'
import RejectedRequests from './widgets/RejectedRequests'
import PieChart from './widgets/PieChart'
import AdminHomeTab from 'app/main/academy/crp/tabs/AdminHomeTab'
import Container from '@material-ui/core/Container';
import CourseList from '../usables/CourseList';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
const useStyles = makeStyles(theme => ({
    content          : {
        '& canvas': {
            maxHeight: '100%'
        }
    },
    selectedProject  : {
        background  : theme.palette.primary.main,
        color       : theme.palette.primary.contrastText,
        borderRadius: '8px 0 0 0'
    },
    projectMenuButton: {
        background  : theme.palette.primary.main,
        color       : theme.palette.primary.contrastText,
        borderRadius: '0 8px 0 0',
        marginLeft  : 1
    },
}));




export default function AdminPage(props)
{
    const [state, setState] = React.useState({
        checkedA: true,
        checkedB: false,
        checkedB: false,
      });

      const handleChange = (event) => {
        setState({ ...state, [event.target.name]: event.target.checked });
      };
      // for push buttons
    const classes = useStyles(props);
    const pageLayout = useRef(null);
    const [selectedTab, setSelectedTab]=useState(0);
    const fcolumns = [
        {
          Header    : "SlNo",
          accessor  : "sno",
          className : "font-bold justify-center",
        },
        {
          Header    : "Name",
          filterable: true,
