import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Checkbox from '@material-ui/core/Checkbox';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import { Card, Paper } from '@material-ui/core';
import OpenMenu from './OpenMenu';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },

  item :{
      height:100,
  },

  heading:{
      fontSize: 20,
      fontWeight:"bold",
  },
  color:{
      backgroundColor : "#b9bfc7",
  }

}));

export default function GradeCard(props){
    const classes = useStyles();
    const [checked, setChecked] = React.useState([1]);

    const handleToggle = (value) => () => {
      const currentIndex = checked.indexOf(value);
      const newChecked = [...checked];

      if (currentIndex === -1) {
        newChecked.push(value);
      } else {
        newChecked.splice(currentIndex, 1);
      }

      setChecked(newChecked);
    };

    return(
            <div>
                      <Paper elevation={1} className={classes.color}>

          <List>
      <ListItem>
        <ListItemText>  <Typography className={classes.heading} >SL No</Typography> </ListItemText>
        <ListItemText>  <Typography className={classes.heading} >Course Name</Typography> </ListItemText>
        <ListItemText>  <Typography className={classes.heading} >Enrolment</Typography> </ListItemText>
        <ListItemText>  <Typography className={classes.heading} >Enrol. Status</Typography> </ListItemText>
        <ListItemText>  <Typography className={classes.heading} >Grade</Typography> </ListItemText>
        <ListItemText>  <Typography className={classes.heading} >Attendance</Typography> </ListItemText>

      </ListItem>
          </List>
      </Paper>
            <br></br>
            <Paper elevation={1}>

            <List dense className={classes.root}>
            {props.item.map((value) => {
                const labelId = `checkbox-list-secondary-label-${value}`;
                return (
                    <div>
                <ListItem className={classes.item} key={value} button>
                    <ListItemText id={labelId} primary={1} />
                    {/* <ListItemText id={labelId} primary= "CS518::Computer Vision::" /> */}

                    <ListItemText

                        primary={`${value.courseCode} :: ${value.courseName}`}
                        secondary={
                            <React.Fragment>
                            <Typography
                                component="span"
                                variant="body2"
                                className={classes.inline}
                            >
                            2-0-2-5-3 (credits {value.credit})

                            </Typography>
                            </React.Fragment>
                        }
                        />


                    <ListItemText id={labelId} primary={value.typeOfEnrolment} />
                    <ListItemText id={labelId} primary={value.status} />

                    <ListItemText id={labelId} primary={value.grade} />
                    <ListItemText id={labelId} primary={value.attendance}/>


                </ListItem>
                            <Divider />

                            </div>
                );
            })}

            </List>
            </Paper>
        </div>
    );

}
