import React, {useEffect, useMemo, useState} from 'react';
import jwtService from 'app/services/jwtService';

import { submitautologin } from 'app/auth/store/actions/login.actions';
import {
    Button,
    Card,
    CardContent,
    OutlinedInput,
    Icon,
    TextField,
    Typography,
    CardActions,
    Divider,
    Select,
    InputLabel,
    FormControl,
    MenuItem,
    LinearProgress
} from '@material-ui/core';
import {makeStyles, useTheme} from '@material-ui/styles';
import {FuseAnimate, FuseAnimateGroup} from '@fuse';
import {useDispatch, useSelector} from 'react-redux';
import withReducer from 'app/store/withReducer';
import clsx from 'clsx';
import _ from '@lodash';
import {Link} from 'react-router-dom';
import * as Actions from '../store/actions';
import reducer from '../store/reducers';



const useStyles = makeStyles(theme => ({
    header    : {
        background: 'linear-gradient(to right, ' + theme.palette.primary.dark + ' 0%, ' + theme.palette.primary.main + ' 100%)',
        color     : theme.palette.getContrastText(theme.palette.primary.main)
    },
    headerIcon: {
        position     : 'absolute',
        top          : -64,
        left         : 0,
        opacity      : .04,
        fontSize     : 512,
        width        : 512,
        height       : 512,
        pointerEvents: 'none'
    }
}));

function AcademyPage()
{

    const classes=useStyles();
    return (
        <div className="flex flex-col flex-1 w-full">


            <div
                className={clsx(classes.header, "relative overflow-hidden flex flex-col flex-shrink-0 items-center justify-center text-center p-16 sm:p-24 h-200 sm:h-288")}>

                <FuseAnimate animation="transition.slideUpIn" duration={400} delay={100}>
                    <Typography color="inherit" className="text-24 sm:text-40 font-light">
                        WELCOME TO ACADEMY
                    </Typography>
                </FuseAnimate>

                <FuseAnimate duration={400} delay={600}>
                    <Typography variant="subtitle1" color="inherit" className="mt-8 sm:mt-16 mx-auto max-w-512">
                            <span className="opacity-75">
                                One Stop Solution for all your Academic Needs
                            </span>
                    </Typography>
                </FuseAnimate>

                <Icon className={classes.headerIcon}>school</Icon>
            </div>
            <FuseAnimateGroup
                enter={{
                    animation: "transition.slideUpBigIn"
                }}
                className="flex flex-wrap py-24"
            >
            <div className="w-full pb-24 sm:w-1/2 lg:w-1/3 sm:p-16" >
                <Card 
                elevation={1} className="flex flex-col h-256">

                    <CardContent className="flex flex-col flex-auto items-center justify-center">
                        <Typography className="text-center text-16 font-400">Course Registration Portal</Typography>
                    </CardContent>
                    {/* <Divider/> */}
                        <CardActions className="justify-center">
                            <Button
                             to={`/academy/crp`}
                            component={Link}
                            className="justify-start px-32"
                            color="secondary"
                        >
                            Enter 
                        </Button>
                    </CardActions> 

                </Card>
            </div>
            <div className="w-full pb-24 sm:w-1/2 lg:w-1/3 sm:p-16" >
                <Card 
                elevation={1} className="flex flex-col h-256">

                    <CardContent className="flex flex-col flex-auto items-center justify-center">
                        <Typography className="text-center text-16 font-400">Classroom Management System</Typography>
                    </CardContent>
                    {/* <Divider/> */}
                        <CardActions className="justify-center">
                            <Button
                            to = {`/academy/courses`}
                            component={Link}
                            className="justify-start px-32"
                            color="secondary"
                        >
                            Enter 
                        </Button>
                    </CardActions> 

                </Card>
            </div>

           </FuseAnimateGroup>
        </div>
    );
}

export default AcademyPage;
