import React, { useEffect, useState } from 'react';
import { Avatar, Button, Tab, Tabs, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { FusePageSimple, FuseAnimate } from '@fuse';
import axiosInstance from 'app/main/axios';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import { Editor } from '@tinymce/tinymce-react';
import store from 'app/store';
import jwtService from 'app/services/jwtService/jwtService'
import { Redirect } from 'react-router-dom';
import Enrolments from './tabs/CourseDetails/Enrolments';
import { useSelector, useDispatch } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import CourseDet from './tabs/CourseDetails/CourseDet';
import CourseDetails from './tabs/CourseDetails/CourseDetails';
import PastOfferings from './tabs/CourseDetails/PastOfferings';

const useStyles = makeStyles(theme => ({
    layoutHeader: {
        height: 200,
        minHeight: 200,
        [theme.breakpoints.down('md')]: {
            height: 240,
            minHeight: 240
        }
    }
}));




export default function CoursePage(props) {
    const classes = useStyles();
    const [selectedTab, setSelectedTab] = useState(0);

    const user = useSelector(({ auth }) => auth.user);
    console.log(user);
    useEffect(() => {

    },[props])

    function handleTabChange(event, value) {
        setSelectedTab(value);
    }

    //  if (jwtService.isAuthTokenValid())
          return (

             <FusePageSimple 
                classes={{
                    header: classes.layoutHeader,
                    toolbar: "px-16 sm:px-24"
                }}
            header= {
                <div className="p-24 flex flex-1 flex-col items-center justify-center md:flex-row md:items-end">
                    <div className="flex flex-1 flex-col items-center justify-center md:flex-row md:items-center md:justify-start">
                        <FuseAnimate animation="transition.expandIn" delay={300}>
                            <Avatar className="w-96 h-96" src="assets/images/avatars/Velazquez.jpg"/>
                        </FuseAnimate>
                        <FuseAnimate animation="transition.slideLeftIn" delay={300}>
                            <Typography className="md:ml-24" variant="h4" color="inherit">Machine Learning</Typography>
                        </FuseAnimate>
                    </div>
                    <div className="flex items-center ">
                        <Button className=" normal-case"  variant="contained" color="secondary" aria-label="Ask Question">Enrol</Button>
                         <p>...............</p> 
                    </div>    
                </div>

            }
            contentToolbar={
                <Tabs
                    value={selectedTab}
                    onChange={handleTabChange}
                    indicatorColor="secondary"
                    textColor="secondary"
                    variant="scrollable"
                    scrollButtons="off"
                    classes={{
                        root: "h-64 w-full border-b-1"
                    }}
                >
                    <Tab
                        classes={{
                            root: "h-64"
                        }}
                        label="Course Details" />
                    <Tab
                        classes={{
                            root: "h-64"
                        }} label="Enrolment Statistics" />
                    <Tab
                        classes={{
                            root: "h-64"
                        }} label="Past Offerings" />


                </Tabs>
            }

            content={

                <div className="p-16 sm:p-24">
                    {selectedTab === 0 &&
                        (
                        <CourseDetails offeringId={props.match.params.offeringId}/>
                        )}
                    {selectedTab === 1 && (
                       <Enrolments offeringId={propsmatch.params.offeringId}/>
                    )}  
                    {selectedTab === 2 && (
                       <PastOfferings offeringId={props.params.offeringId}/>
                    )} 
                </div>


            }



             >
             </FusePageSimple>
         )
    // else
    //     return (<Redirect to='/login' />)
}
