import * as Actions from '../actions';

const initialState = {
    data      : null,
    categories: []
};

const coursesReducer = function (state = initialState, action) {
    switch ( action.type )
    {
        case Actions.GET_COURSES:
        {
            return {
                ...state,
                data: action.payload
            };
        }

        default:
        {
            return state;
        }
    }
};

export default coursesReducer;
