import React, { useEffect, useMemo, useState } from 'react';
import {
    Button,
    Card,
    CardContent,
    OutlinedInput,
    Icon,
    TextField,
    Typography,
    CardActions,
    Divider,
    Select,
    InputLabel,
    FormControl,
    MenuItem,
    LinearProgress
} from '@material-ui/core';
import { makeStyles, useTheme } from '@material-ui/styles';
import { FuseAnimate, FuseAnimateGroup } from '@fuse';
import { useDispatch, useSelector } from 'react-redux';
import withReducer from 'app/store/withReducer';
import clsx from 'clsx';
import _ from '@lodash';
import { Link } from 'react-router-dom';
import axiosInstance from 'app/main/axios';
import RequestList from './RequestList';


const useStyles = makeStyles(theme => ({
    header: {
        background: 'linear-gradient(to right, ' + theme.palette.primary.dark + ' 0%, ' + theme.palette.primary.main + ' 100%)',
        color: theme.palette.getContrastText(theme.palette.primary.main)
    },
    headerIcon: {
        position: 'absolute',
        top: -64,
        left: 0,
        opacity: .04,
        fontSize: 512,
        width: 512,
        height: 512,
        pointerEvents: 'none'
    }
}));

export default function PendingCourses(props) {
    const classes = useStyles(props);
    const theme = useTheme();
    const [offeringData, setOfferingData]=React.useState([]);
    const [offeringChosen,setOfferingChosen]=React.useState([]);
    const [data , setEnrollmentData] = React.useState([])
    const [open,setOpen] = React.useState(false);
    function handleClick(id , Coursecode){

        setOfferingChosen(Coursecode)
        axiosInstance.get('get-enrollment-data/', {
            params: {
              offeringId: id
            }
          }
          ).then(
            (response) => {
              console.log("enrollment data",response.data)
              const enrolments = []
              const list=[]
              for(var i=0;i<response.data.length;i++){
                var value = response.data[i];
                enrolments.push
                list.push(
                  { 'sno' : i+1 ,
                    // 'enrolmentId':value.id,
                    'entry_no':value.studentId__rollNum,
                    'name':`${value.studentId__userId__first_name} ${value.studentId__userId__last_name}`,
                    'enrol_type' :value.enrolType,
                    'cgpa':8.4,
                    'dept':value.studentId__departmentId__departmentName,
                    'spec':"Conc in AI"  ,
                    'credits_completed':"100" ,
                    'graduation':value.studentId__graduationYear,
                    'status': value.status
                  }
                )
              }
              setEnrollmentData(list);
            }
          );

    }


    function checkOngoing(courseOffering) {
    return courseOffering.offeringId__status != 'Completed';
    }

    useEffect(()=>
        {
            axiosInstance.get('get-current-course-teacher/').then((res)=>
                {
                    console.log("hellocourse");
                    console.log(res.data);
                    setOfferingData(res.data);


                }
            )
        }
  ,[] )

    return (
        <div>
            <div className="flex flex-col flex-1 max-w-2xl w-full mx-auto px-8 sm:px-16 py-24">
                {useMemo(() => (
                offeringData && offeringData.length > 0 && (
                    (offeringData.filter(checkOngoing).length > 0) ? (
                            <FuseAnimateGroup
                                enter={{
                                    animation: "transition.slideUpBigIn"
                                }}
                                className="flex flex-wrap py-24"
                            >
                                {offeringData.filter(checkOngoing).map((offering) => {
                                    return (
                                        <div className="w-full pb-24 sm:w-1/2 lg:w-1/3 sm:p-16" >
                                            <Card elevation={1} className="flex flex-col h-256">
                                            <div
                                            className="flex flex-shrink-0 items-center justify-between px-24 h-64"
                                            style={{
                                                background: '#8BC34A',
                                                color: '#000000'
                                            }}
                                            >

                                            <div className="flex items-center justify-center opacity-75">
                                                <Icon className="text-20 mr-8" color="inherit">access_time</Icon>
                                                <div className="text-16 whitespace-no-wrap">{offering.offeringId__status}</div>
                                            </div>
                                            </div>
                                            <CardContent className="flex flex-col flex-auto items-center justify-center">
                                            <Typography className="text-center text-16 font-400">{offering.offeringId__courseId__courseCode}</Typography>
                                            <Typography className="text-center text-13 font-600 mt-4" color="textSecondary">{offering.offeringId__session} Sem-{offering.offeringId__sem}</Typography>
                                            </CardContent>
                                            <Divider />
                                            <CardActions className="justify-center">
                                            <Button
                                            onClick={()=>handleClick(offering.offeringId ,offering.offeringId__courseId__courseCode)}   
                                            >See More
                                            </Button>
                                            </CardActions>
                                            <LinearProgress
                                            className="w-full"
                                            variant="determinate"
                                            color="secondary"
                                            value={30}
                                            />
                                        </Card>

                                        </div>

                                    )
                                })}
                            </FuseAnimateGroup>
                        ) :
                            (
                                <div className="flex flex-1 items-center justify-center">
                                    <Typography color="textSecondary" className="text-24 my-24">
                                        No courses found!
                                    </Typography>
                                </div>
                            )
                    )), [offeringData, theme.palette])}
            </div>
       {
           data.length>0?
       <RequestList courseName={offeringChosen} data = {data} />:null
       }  
        </div>
    );
}
